  async updateCampaignStatus(id: number, status: string): Promise<Campaign | undefined> {
    const [campaign] = await db
      .update(campaigns)
      .set({ 
        status,
        sentAt: status === 'sent' ? new Date() : undefined
      })
      .where(eq(campaigns.id, id))
      .returning();
    return campaign;
  }

