      res.status(500).json({ message: "Failed to record conversion" });
    }
  });

  app.get(`${apiRoute}/ab-tests/:testId/results`, isAuthenticated, isAdmin, async (req, res) => {
    try {
      const testId = parseInt(req.params.testId);
      if (isNaN(testId)) {
        return res.status(400).json({ message: "Invalid test ID format" });
      }

      const results = await storage.getABTestResults(testId);
      res.json(results);
    } catch (error) {
      console.error(`Error fetching results for test ID ${req.params.testId}:`, error);
      res.status(500).json({ message: "Failed to fetch test results" });
    }
  });

  // Initialize all payment gateways
  const paymentGateways = initPaymentGateways();
  console.log('Payment gateways initialized:', 
    Object.entries(paymentGateways)
      .filter(([key]) => key !== 'anyInitialized')
      .map(([key, value]) => `${key}: ${value ? 'Yes' : 'No'}`)
      .join(', ')
  );

  // Payment gateways status endpoint
  app.get(`${apiRoute}/payment/status`, (req, res) => {
    getPaymentGatewaysStatus(req, res);
  });

  // Stripe payment endpoints
  app.post(`${apiRoute}/payment/stripe/create-intent`, (req, res) => {
    createPaymentIntent(req, res);
  });

  app.post(`${apiRoute}/payment/stripe/webhook`, (req, res) => {
    handleStripeWebhook(req, res);
  });

  // PayPal payment endpoints
  app.get(`${apiRoute}/payment/paypal/setup`, (req, res) => {
    setupPaypalClient(req, res);
  });

  app.post(`${apiRoute}/payment/paypal/create-order`, (req, res) => {
    createPaypalOrder(req, res);
  });

  app.post(`${apiRoute}/payment/paypal/capture/:orderID`, (req, res) => {
    capturePaypalOrder(req, res);
  });

  // Razorpay payment endpoints
  app.post(`${apiRoute}/payment/razorpay/create-order`, (req, res) => {
    createRazorpayOrder(req, res);
  });

  app.post(`${apiRoute}/payment/razorpay/verify`, (req, res) => {
    verifyRazorpayPayment(req, res);
  });

  app.get(`${apiRoute}/payment/razorpay/payment/:payment_id`, (req, res) => {
    getRazorpayPaymentDetails(req, res);
  });

  // UPI direct payment endpoints
  app.get(`${apiRoute}/payment/upi/info`, (req, res) => {
    upiHandler.getUpiInfo(req, res);
  });

  app.post(`${apiRoute}/payment/upi/verify`, (req, res) => {
    upiHandler.verifyPayment(req, res);
  });

  app.get(`${apiRoute}/payment/upi/status/:transactionId`, (req, res) => {
    upiHandler.getPaymentStatus(req, res);
  });

  // Consultation endpoints

  // Create a new consultation booking
  app.post(`${apiRoute}/consultations`, async (req, res) => {
    try {
      const validatedData = insertConsultationSchema.parse(req.body);
      const consultation = await storage.createConsultation(validatedData);

      // Add submitter to recipients list
      try {
        // Check if recipient already exists
        const existingRecipient = await storage.getRecipientByEmail(validatedData.email);

        if (!existingRecipient) {
          // Add to recipients if they don't exist
          await storage.createRecipient({
            name: validatedData.name,
            email: validatedData.email,
            phone: validatedData.phone,
            source: 'consultation',
            tags: ['lead', 'consultation'],
            unsubscribed: false,
            metadata: {
              topic: validatedData.topic,
              consultationDate: validatedData.date,
              timeSlot: validatedData.timeSlot,
              bookingDate: new Date().toISOString()
            }
          });
          console.log('📊 Added consultation submitter to recipients list');
        }
      } catch (recipientError) {
        console.error('Error adding consultation submitter to recipients:', recipientError);
        // Don't fail the request if adding to recipients fails
      }

      res.status(201).json({ 
        message: "Consultation booking request received",
        id: consultation.id,
        consultation
      });
    } catch (error) {
      handleValidationError(error, res);
    }
  });

  // Admin consultation management endpoints
  app.get(`${apiRoute}/admin/consultations`, isAuthenticated, isAdmin, getAllConsultations);
  app.get(`${apiRoute}/admin/consultations/:id`, isAuthenticated, isAdmin, getConsultationById);
  app.patch(`${apiRoute}/admin/consultations/:id/status`, isAuthenticated, isAdmin, updateConsultationStatus);
  app.patch(`${apiRoute}/admin/consultations/:id/payment-status`, isAuthenticated, isAdmin, updateConsultationPaymentStatus);
  app.patch(`${apiRoute}/admin/consultations/:id`, isAuthenticated, isAdmin, updateConsultation);
  app.delete(`${apiRoute}/admin/consultations/:id`, isAuthenticated, isAdmin, deleteConsultation);
  app.post(`${apiRoute}/admin/consultations/:id/send-reminder`, isAuthenticated, isAdmin, sendReminder);

  // Client-facing consultation endpoints

  // Public endpoint to get consultation details by ID (needed for payment page)
  app.get(`${apiRoute}/consultations/:id`, async (req, res) => {
    try {
      const consultationId = parseInt(req.params.id);

      if (isNaN(consultationId)) {
        return res.status(400).json({ message: "Invalid consultation ID" });
      }

      const consultation = await storage.getConsultationById(consultationId);

      if (!consultation) {
        return res.status(404).json({ message: "Consultation not found" });
      }

      res.json({ consultation });
    } catch (error) {
      console.error("Error fetching consultation:", error);
      res.status(500).json({ message: "Failed to fetch consultation" });
    }
  });

  // Client-facing consultation payment endpoint
  app.post(`${apiRoute}/consultations/:id/process-payment`, processConsultationPayment);

  const httpServer = createServer(app);
  return httpServer;
}

