import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  Loader2,
  PlusCircle,
  Trash2,
  Edit,
  Eye,
  GridIcon,
  ListIcon,
  Search,
  Calendar,
  Tag,
  User,
  Clock,
  CheckIcon,
  XCircle,
  Bookmark,
  Filter,
  FilePlus,
  FileText,
  Pencil
} from "lucide-react";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Card,
  CardHeader,
  CardTitle,
  CardDescription,
  CardContent,
  CardFooter,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Badge } from "@/components/ui/badge";
import { apiRequest, queryClient } from "@/lib/queryClient";
import {
  Table,
  TableBody,
  TableCaption,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "@/components/ui/tabs";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Switch } from "@/components/ui/switch";
import MediaLibrarySelector from "@/components/admin/media-library-selector";

const blogPostSchema = z.object({
  title: z.string().min(3, "Title must be at least 3 characters long"),
  slug: z.string().min(3, "Slug must be at least 3 characters long")
    .regex(/^[a-z0-9-]+$/, "Slug can only contain lowercase letters, numbers, and hyphens"),
  excerpt: z.string().min(20, "Excerpt must be at least 20 characters"),
  content: z.string().min(100, "Content must be at least 100 characters"),
  imageUrl: z.string().url("Please enter a valid URL"),
  author: z.string().min(1, "Author is required"),
  publishDate: z.string().refine(
    (date) => !isNaN(Date.parse(date)), 
    { message: "Invalid date format" }
  ),
  categories: z.array(z.string()).min(1, "At least one category is required"),
  tags: z.array(z.string()).min(1, "At least one tag is required"),
  featured: z.boolean().default(false),
});

type BlogPostFormValues = z.infer<typeof blogPostSchema>;

// Sample categories and tags
const sampleCategories = [
  "Web Development",
  "Mobile Development",
  "UI/UX Design",
  "Programming",
  "Marketing",
  "SEO",
  "Technology",
  "Industry News",
  "Tips & Tricks",
  "Case Studies",
  "Tutorials",
  "Business"
];

interface BlogPostCardProps {
  post: any;
  onEdit: (post: any) => void;
  onDelete: (id: number) => void;
  onPreview: (post: any) => void;
  onToggleFeatured: (id: number, featured: boolean) => void;
}

function BlogPostCard({ 
  post, 
  onEdit, 
  onDelete, 
  onPreview, 
  onToggleFeatured 
}: BlogPostCardProps) {
  // Format date for display
  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <Card className="overflow-hidden flex flex-col h-full">
      <div className="relative aspect-[16/9] bg-gray-100 overflow-hidden">
        <img 
          src={post.imageUrl} 
          alt={post.title} 
          className="w-full h-full object-cover"
          onError={(e) => {
            (e.target as HTMLImageElement).src = "https://placehold.co/400x300?text=Image+Not+Found";
          }}
        />
        <div className="absolute bottom-0 right-0 flex gap-1 m-2">
          <Button 
            variant="outline" 
            size="sm"
            className="bg-white/80 backdrop-blur-sm hover:bg-white"
            onClick={() => onPreview(post)}
          >
            <Eye className="h-4 w-4" />
          </Button>
          <Button 
            variant="outline" 
            size="sm"
            className="bg-white/80 backdrop-blur-sm hover:bg-white"
            onClick={() => onEdit(post)}
          >
            <Edit className="h-4 w-4" />
          </Button>
        </div>
      </div>

      <CardHeader className="pb-2">
        <div className="flex justify-between items-start">
          <div>
            <CardTitle className="text-lg mb-1 line-clamp-2">{post.title}</CardTitle>
            <CardDescription className="text-sm flex items-center">
              <Calendar className="h-3 w-3 mr-1" /> {formatDate(post.publishDate)}
            </CardDescription>
          </div>
          {post.featured && <Badge>Featured</Badge>}
        </div>
      </CardHeader>

      <CardContent className="py-2 flex-grow">
        <p className="text-sm line-clamp-3 mb-3">{post.excerpt}</p>

        <div className="flex flex-wrap gap-1.5 mb-2">
          {post.categories && post.categories.slice(0, 2).map((category: string, index: number) => (
            <Badge key={`cat-${index}`} variant="secondary" className="text-xs">
              {category}
            </Badge>
          ))}
          {post.categories && post.categories.length > 2 && (
            <Badge variant="secondary" className="text-xs">
              +{post.categories.length - 2}
            </Badge>
          )}
        </div>

        <div className="flex flex-wrap gap-1">
          {post.tags && post.tags.slice(0, 3).map((tag: string, index: number) => (
            <Badge key={`tag-${index}`} variant="outline" className="text-xs">
              {tag}
            </Badge>
          ))}
          {post.tags && post.tags.length > 3 && (
            <Badge variant="outline" className="text-xs">
              +{post.tags.length - 3}
            </Badge>
          )}
        </div>
      </CardContent>

      <CardFooter className="pt-2 flex justify-between border-t mt-auto">
        <div className="flex items-center text-sm text-gray-500">
          <User className="h-3.5 w-3.5 mr-1.5" />
          {post.author}
        </div>

        <div className="flex gap-1">
          <Button
            variant="ghost"
            size="sm"
            className="h-8 w-8 p-0"
            onClick={() => onToggleFeatured(post.id, !post.featured)}
          >
            {post.featured ? (
              <XCircle className="h-4 w-4 text-gray-500 hover:text-gray-700" />
            ) : (
              <Bookmark className="h-4 w-4 text-gray-500 hover:text-gray-700" />
            )}
          </Button>

          <Button 
            variant="ghost" 
            size="sm"
            className="h-8 w-8 p-0 text-red-600 hover:text-red-700 hover:bg-red-50"
            onClick={() => onDelete(post.id)}
          >
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      </CardFooter>
    </Card>
  );
}

export default function BlogManager() {
  const { toast } = useToast();
  const [isNewPostDialogOpen, setIsNewPostDialogOpen] = useState(false);
  const [isUpdateDialogOpen, setIsUpdateDialogOpen] = useState(false);
  const [isPreviewDialogOpen, setIsPreviewDialogOpen] = useState(false);
  const [selectedPost, setSelectedPost] = useState<any>(null);
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [searchQuery, setSearchQuery] = useState("");
  const [categoryFilter, setCategoryFilter] = useState<string | null>(null);

  // Form states
  const [tagInput, setTagInput] = useState("");
  const [categoryInput, setCategoryInput] = useState("");
  const [updateTagInput, setUpdateTagInput] = useState("");
  const [updateCategoryInput, setUpdateCategoryInput] = useState("");

  const queryClient = useQueryClient();

  const form = useForm<BlogPostFormValues>({
    resolver: zodResolver(blogPostSchema),
    defaultValues: {
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      imageUrl: "",
      author: "",
      publishDate: new Date().toISOString().split('T')[0],
      categories: [],
      tags: [],
      featured: false,
    },
  });

  const updateForm = useForm<BlogPostFormValues>({
    resolver: zodResolver(blogPostSchema),
    defaultValues: {
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      imageUrl: "",
      author: "",
      publishDate: new Date().toISOString().split('T')[0],
      categories: [],
      tags: [],
      featured: false,
    },
  });

  const { data: blogPosts, isLoading, error } = useQuery({
    queryKey: ["/api/blog"],
    refetchOnWindowFocus: false,
  });

  const createPostMutation = useMutation({
    mutationFn: async (data: BlogPostFormValues) => {
      const formattedData = {
        ...data,
        publishDate: new Date(data.publishDate)
      };
      const res = await apiRequest("POST", "/api/blog", formattedData);
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Blog post created",
        description: "Your blog post has been created successfully.",
      });
      form.reset();
      setIsNewPostDialogOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error creating blog post",
        description: error.message || "An error occurred while creating the blog post.",
        variant: "destructive",
      });
    },
  });

  const updatePostMutation = useMutation({
    mutationFn: async (data: BlogPostFormValues & { id: number }) => {
      const { id, ...postData } = data;
      const formattedData = {
        ...postData,
        publishDate: new Date(postData.publishDate)
      };
      const res = await apiRequest("PUT", `/api/blog/${id}`, formattedData);
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Blog post updated",
        description: "Your blog post has been updated successfully.",
      });
      updateForm.reset();
      setIsUpdateDialogOpen(false);
      setSelectedPost(null);
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error updating blog post",
        description: error.message || "An error occurred while updating the blog post.",
        variant: "destructive",
      });
    },
  });

  const deletePostMutation = useMutation({
    mutationFn: async (id: number) => {
      const res = await apiRequest("DELETE", `/api/blog/${id}`);
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Blog post deleted",
        description: "The blog post has been deleted.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error deleting blog post",
        description: error.message || "An error occurred while deleting the blog post.",
        variant: "destructive",
      });
    },
  });

  const toggleFeaturedMutation = useMutation({
    mutationFn: async ({ id, featured }: { id: number; featured: boolean }) => {
      const res = await apiRequest("PATCH", `/api/blog/${id}`, { featured });
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Blog post updated",
        description: "Featured status has been updated.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error updating blog post",
        description: error.message || "An error occurred while updating the blog post.",
        variant: "destructive",
      });
    },
  });

  // Tag and category management for the create form
  const handleAddTag = () => {
    if (tagInput.trim()) {
      const currentTags = form.getValues().tags || [];
      if (!currentTags.includes(tagInput.trim())) {
        form.setValue("tags", [...currentTags, tagInput.trim()]);
      }
      setTagInput("");
    }
  };

  const handleRemoveTag = (tag: string) => {
    const currentTags = form.getValues().tags || [];
    form.setValue("tags", currentTags.filter(t => t !== tag));
  };

  const handleAddCategory = () => {
    if (categoryInput.trim()) {
      const currentCategories = form.getValues().categories || [];
      if (!currentCategories.includes(categoryInput.trim())) {
