            <SelectContent>
              <SelectItem value="">All Categories</SelectItem>
              {uniqueCategories.map(category => (
                <SelectItem key={category} value={category}>{category}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="flex items-center space-x-2">
          <Button
            variant={viewMode === 'grid' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('grid')}
          >
            <GridIcon className="h-4 w-4 mr-1" />
            Grid
          </Button>
          <Button
            variant={viewMode === 'list' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('list')}
          >
            <ListIcon className="h-4 w-4 mr-1" />
            List
          </Button>
        </div>
      </div>

      {isLoading ? (
        <div className="flex justify-center items-center h-64">
          <Loader2 className="h-8 w-8 animate-spin" />
        </div>
      ) : error ? (
        <div className="bg-red-50 border border-red-200 text-red-700 p-4 rounded-md">
          <p>Error loading blog posts: {(error as Error).message}</p>
        </div>
      ) : blogPosts && blogPosts.length === 0 ? (
        <div className="text-center py-12 border-2 border-dashed border-gray-200 rounded-md">
          <FileText className="h-12 w-12 mx-auto text-gray-400 mb-3" />
          <h3 className="text-lg font-medium mb-2">No blog posts yet</h3>
          <p className="text-gray-500 mb-4">Get started by creating your first blog post</p>
          <Button onClick={() => setIsNewPostDialogOpen(true)}>
            <FilePlus className="mr-2 h-4 w-4" /> Write Your First Post
          </Button>
        </div>
      ) : filteredPosts.length === 0 ? (
        <div className="text-center py-12 border-2 border-dashed border-gray-200 rounded-md">
          <Filter className="h-12 w-12 mx-auto text-gray-400 mb-3" />
          <h3 className="text-lg font-medium mb-2">No matching posts</h3>
          <p className="text-gray-500 mb-4">Try adjusting your search query or filters</p>
          <Button variant="outline" onClick={() => {
            setSearchQuery("");
            setCategoryFilter(null);
          }}>
            Clear Filters
          </Button>
        </div>
      ) : viewMode === 'grid' ? (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          {sortedPosts.map((post: any) => (
            <BlogPostCard
              key={post.id}
              post={post}
              onEdit={handleEditPost}
              onDelete={handleDeletePost}
              onPreview={handlePreviewPost}
              onToggleFeatured={handleToggleFeatured}
            />
          ))}
        </div>
      ) : (
        <div className="border rounded-md">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="w-[80px]">Image</TableHead>
                <TableHead>Title</TableHead>
                <TableHead>Author</TableHead>
                <TableHead className="hidden md:table-cell">Date</TableHead>
                <TableHead className="hidden md:table-cell">Categories</TableHead>
                <TableHead className="w-[100px] text-center">Featured</TableHead>
                <TableHead className="w-[100px] text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {sortedPosts.map((post: any) => (
                <TableRow key={post.id}>
                  <TableCell>
                    <div className="w-16 h-16 bg-gray-100 rounded flex items-center justify-center overflow-hidden">
                      <img 
                        src={post.imageUrl} 
                        alt={post.title} 
                        className="max-w-full max-h-full object-cover"
                        onError={(e) => {
                          (e.target as HTMLImageElement).src = "https://placehold.co/400x300?text=Error";
                        }}
                      />
                    </div>
                  </TableCell>
                  <TableCell className="font-medium">
                    <div className="max-w-xs">
                      <div className="truncate">{post.title}</div>
                      <div className="text-xs text-gray-500 truncate">/blog/{post.slug}</div>
                    </div>
                  </TableCell>
                  <TableCell>{post.author}</TableCell>
                  <TableCell className="hidden md:table-cell">{formatDate(post.publishDate)}</TableCell>
                  <TableCell className="hidden md:table-cell">
                    <div className="flex flex-wrap gap-1">
                      {post.categories && post.categories.slice(0, 2).map((category: string, index: number) => (
                        <Badge key={index} variant="secondary" className="text-xs">
                          {category}
                        </Badge>
                      ))}
                      {post.categories && post.categories.length > 2 && (
                        <Badge variant="secondary" className="text-xs">
                          +{post.categories.length - 2}
                        </Badge>
                      )}
                    </div>
                  </TableCell>
                  <TableCell className="text-center">
                    {post.featured ? 
                      <CheckIcon className="h-5 w-5 text-green-500 mx-auto" /> : 
                      <XCircle className="h-5 w-5 text-gray-300 mx-auto" />
                    }
                  </TableCell>
                  <TableCell className="text-right">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="sm">
                          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-more-vertical"><circle cx="12" cy="12" r="1"/><circle cx="12" cy="5" r="1"/><circle cx="12" cy="19" r="1"/></svg>
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem onClick={() => handlePreviewPost(post)}>
                          <Eye className="mr-2 h-4 w-4" />
                          Preview
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleEditPost(post)}>
                          <Edit className="mr-2 h-4 w-4" />
                          Edit
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleToggleFeatured(post.id, !post.featured)}>
                          {post.featured ? 
                            <>
                              <XCircle className="mr-2 h-4 w-4" />
                              Remove Featured
                            </> :
                            <>
                              <Bookmark className="mr-2 h-4 w-4" />
                              Mark as Featured
                            </>
                          }
                        </DropdownMenuItem>
                        <DropdownMenuSeparator />
                        <DropdownMenuItem 
                          className="text-red-600" 
                          onClick={() => handleDeletePost(post.id)}
                        >
                          <Trash2 className="mr-2 h-4 w-4" />
                          Delete
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}
    </div>
  );
}

client/src/components/admin/consultation.content:
import React, { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { format } from "date-fns";
import { useToast } from "@/hooks/use-toast";

import {
  Table,
  TableBody,
  TableCaption,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Loader2, Search, Plus, Calendar, ClipboardCheck, Trash, Mail, CheckCircle, XCircle, AlertCircle, Clock } from "lucide-react";

interface Consultation {
  id: number;
  name: string;
  email: string;
  phone: string;
  date: string;
  timeSlot: string;
  topic: string;
  additionalInfo: string | null;
  status: string;
  paymentStatus: string;
  paymentMethod: string | null;
  paymentId: string | null;
  paymentAmount: number;
  meetingLink: string | null;
  notes: string | null;
  createdAt: string;
  updatedAt: string;
}

export default function ConsultationManager() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedConsultation, setSelectedConsultation] = useState<Consultation | null>(null);
  const [viewMode, setViewMode] = useState<'details' | 'edit'>('details');
  const [meetingLink, setMeetingLink] = useState("");
  const [notes, setNotes] = useState("");
  const [isDetailsDialogOpen, setIsDetailsDialogOpen] = useState(false);
  const { toast } = useToast();

  // Fetch consultations
  const { data: consultations, isLoading, error } = useQuery({
    queryKey: ['/api/admin/consultations'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/admin/consultations');
      if (!response.ok) {
        throw new Error('Failed to fetch consultations');
      }
      return response.json();
    }
  });

  // Update consultation status
  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: number; status: string }) => {
      const response = await apiRequest('PATCH', `/api/admin/consultations/${id}/status`, { status });
      if (!response.ok) {
        throw new Error('Failed to update consultation status');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/consultations'] });
      toast({
        title: "Status updated",
        description: "The consultation status has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // Update consultation details (meeting link, notes)
  const updateDetailsMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: any }) => {
      const response = await apiRequest('PATCH', `/api/admin/consultations/${id}`, data);
      if (!response.ok) {
        throw new Error('Failed to update consultation details');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/consultations'] });
      setViewMode('details');
      toast({
        title: "Details updated",
        description: "The consultation details have been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // Delete consultation
  const deleteConsultationMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest('DELETE', `/api/admin/consultations/${id}`);
      if (!response.ok) {
        throw new Error('Failed to delete consultation');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/consultations'] });
      setSelectedConsultation(null);
      setIsDetailsDialogOpen(false);
      toast({
        title: "Deleted",
        description: "The consultation has been deleted successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Delete failed",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // Send reminder email
  const sendReminderMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest('POST', `/api/admin/consultations/${id}/send-reminder`);
      if (!response.ok) {
        throw new Error('Failed to send reminder');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Reminder sent",
        description: "A reminder email has been sent to the client.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to send reminder",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  const handleViewConsultation = (consultation: Consultation) => {
    setSelectedConsultation(consultation);
    setMeetingLink(consultation.meetingLink || "");
    setNotes(consultation.notes || "");
    setViewMode('details');
    setIsDetailsDialogOpen(true);
  };

  const handleEditConsultation = () => {
    setViewMode('edit');
  };

  const handleSaveDetails = () => {
    if (!selectedConsultation) return;

    updateDetailsMutation.mutate({
      id: selectedConsultation.id,
      data: {
        meetingLink,
        notes
      }
    });
  };

  const handleStatusChange = (id: number, status: string) => {
    updateStatusMutation.mutate({ id, status });
  };

  const handleDelete = () => {
    if (!selectedConsultation) return;
    if (window.confirm('Are you sure you want to delete this consultation?')) {
      deleteConsultationMutation.mutate(selectedConsultation.id);
    }
  };

  const handleSendReminder = () => {
    if (!selectedConsultation) return;
    sendReminderMutation.mutate(selectedConsultation.id);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200">Pending</Badge>;
      case 'confirmed':
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">Confirmed</Badge>;
      case 'completed':
        return <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200">Completed</Badge>;
      case 'cancelled':
        return <Badge variant="outline" className="bg-red-50 text-red-700 border-red-200">Cancelled</Badge>;
      case 'no-show':
        return <Badge variant="outline" className="bg-gray-50 text-gray-700 border-gray-200">No-Show</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const getPaymentStatusBadge = (status: string) => {
    switch (status) {
      case 'paid':
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">Paid</Badge>;
      case 'unpaid':
        return <Badge variant="outline" className="bg-red-50 text-red-700 border-red-200">Unpaid</Badge>;
      case 'refunded':
        return <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200">Refunded</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const filteredConsultations = consultations?.filter((consultation: Consultation) => {
    const searchTermLower = searchTerm.toLowerCase();
    return (
      consultation.name.toLowerCase().includes(searchTermLower) ||
      consultation.email.toLowerCase().includes(searchTermLower) ||
      consultation.phone.toLowerCase().includes(searchTermLower) ||
      consultation.topic.toLowerCase().includes(searchTermLower) ||
      consultation.status.toLowerCase().includes(searchTermLower) ||
      consultation.paymentStatus.toLowerCase().includes(searchTermLower)
    );
  }) || [];

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <Loader2 className="w-8 h-8 animate-spin text-primary" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4 border border-red-200 rounded-md bg-red-50 text-red-700">
        <p>Error loading consultations: {error instanceof Error ? error.message : 'Unknown error'}</p>
        <Button 
          variant="outline" 
          onClick={() => queryClient.invalidateQueries({ queryKey: ['/api/admin/consultations'] })}
          className="mt-2"
        >
          Retry
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Manage Consultations</h2>
        <div className="flex items-center gap-2">
          <div className="relative">
            <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-muted-foreground" />
            <Input
              type="search"
              placeholder="Search consultations..."
              className="pl-9"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <Button variant="outline" onClick={() => window.location.href = '/consultation'}>
            <Plus className="h-4 w-4 mr-2" />
            New Consultation
          </Button>
        </div>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>All Consultations</CardTitle>
          <CardDescription>Manage all consultation bookings and their status</CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Client</TableHead>
                <TableHead>Date & Time</TableHead>
                <TableHead>Topic</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Payment</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredConsultations.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={6} className="text-center py-6 text-muted-foreground">
                    {searchTerm ? 'No consultations match your search.' : 'No consultations have been booked yet.'}
                  </TableCell>
                </TableRow>
              ) : (
                filteredConsultations.map((consultation: Consultation) => (
                  <TableRow key={consultation.id}>
                    <TableCell>
                      <div>
                        <p className="font-medium">{consultation.name}</p>
                        <p className="text-sm text-muted-foreground">{consultation.email}</p>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center">
                        <Calendar className="h-4 w-4 mr-2 text-muted-foreground" />
                        <div>
                          <p>{new Date(consultation.date).toLocaleDateString()}</p>
                          <p className="text-sm text-muted-foreground">
                            {consultation.timeSlot}
                          </p>
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <p className="truncate max-w-[200px]">{consultation.topic}</p>
                    </TableCell>
                    <TableCell>
                      <Select
                        defaultValue={consultation.status}
                        onValueChange={(value) => handleStatusChange(consultation.id, value)}
                      >
                        <SelectTrigger className="w-[130px]">
                          <SelectValue>{getStatusBadge(consultation.status)}</SelectValue>
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="pending">
                            <div className="flex items-center">
                              <Clock className="h-4 w-4 mr-2 text-yellow-600" />
                              <span>Pending</span>
                            </div>
                          </SelectItem>
                          <SelectItem value="confirmed">
                            <div className="flex items-center">
                              <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                              <span>Confirmed</span>
                            </div>
                          </SelectItem>
                          <SelectItem value="completed">
                            <div className="flex items-center">
                              <ClipboardCheck className="h-4 w-4 mr-2 text-blue-600" />
                              <span>Completed</span>
                            </div>
                          </SelectItem>
                          <SelectItem value="cancelled">
                            <div className="flex items-center">
                              <XCircle className="h-4 w-4 mr-2 text-red-600" />
                              <span>Cancelled</span>
                            </div>
                          </SelectItem>
                          <SelectItem value="no-show">
                            <div className="flex items-center">
                              <AlertCircle className="h-4 w-4 mr-2 text-gray-600" />
                              <span>No-Show</span>
                            </div>
                          </SelectItem>
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell>
                      {getPaymentStatusBadge(consultation.paymentStatus)}
                      {consultation.paymentMethod && (
                        <p className="text-xs text-muted-foreground mt-1">via {consultation.paymentMethod}</p>
                      )}
                    </TableCell>
                    <TableCell className="text-right">
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        onClick={() => handleViewConsultation(consultation)}
                      >
                        View Details
                      </Button>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Consultation Details Dialog */}
      {selectedConsultation && (
        <Dialog open={isDetailsDialogOpen} onOpenChange={setIsDetailsDialogOpen}>
          <DialogContent className="max-w-3xl">
            <DialogHeader>
              <DialogTitle className="text-xl">
                {viewMode === 'details' ? 'Consultation Details' : 'Edit Consultation'}
              </DialogTitle>
              <DialogDescription>
                {viewMode === 'details' 
                  ? 'Review the details of this consultation booking.' 
                  : 'Update the meeting link and notes for this consultation.'}
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-6">
              {viewMode === 'details' ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <h3 className="text-sm font-medium text-muted-foreground">Client Information</h3>
                      <div className="mt-2 space-y-2">
                        <div>
                          <p className="font-medium">{selectedConsultation.name}</p>
                          <p className="text-sm">{selectedConsultation.email}</p>
                          <p className="text-sm">{selectedConsultation.phone}</p>
                        </div>
                      </div>
                    </div>

                    <div>
                      <h3 className="text-sm font-medium text-muted-foreground">Booking Details</h3>
                      <div className="mt-2 space-y-1">
                        <div className="flex justify-between">
                          <span className="text-sm">Date:</span>
                          <span className="text-sm font-medium">{new Date(selectedConsultation.date).toLocaleDateString()}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-sm">Time:</span>
                          <span className="text-sm font-medium">{selectedConsultation.timeSlot}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-sm">Status:</span>
                          <span>{getStatusBadge(selectedConsultation.status)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-sm">Created:</span>
                          <span className="text-sm font-medium">{new Date(selectedConsultation.createdAt).toLocaleString()}</span>
                        </div>
                      </div>
                    </div>

                    <div>
                      <h3 className="text-sm font-medium text-muted-foreground">Payment Information</h3>
                      <div className="mt-2 space-y-1">
                        <div className="flex justify-between">
                          <span className="text-sm">Status:</span>
                          <span>{getPaymentStatusBadge(selectedConsultation.paymentStatus)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-sm">Amount:</span>
                          <span className="text-sm font-medium">₹{selectedConsultation.paymentAmount}</span>
                        </div>
                        {selectedConsultation.paymentMethod && (
                          <div className="flex justify-between">
                            <span className="text-sm">Method:</span>
                            <span className="text-sm font-medium">{selectedConsultation.paymentMethod}</span>
                          </div>
                        )}
                        {selectedConsultation.paymentId && (
                          <div className="flex justify-between">
                            <span className="text-sm">Payment ID:</span>
                            <span className="text-sm font-medium truncate max-w-[200px]">{selectedConsultation.paymentId}</span>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <h3 className="text-sm font-medium text-muted-foreground">Consultation Topic</h3>
                      <p className="mt-2 p-3 bg-muted rounded-md text-sm">{selectedConsultation.topic}</p>
                    </div>

                    {selectedConsultation.additionalInfo && (
                      <div>
                        <h3 className="text-sm font-medium text-muted-foreground">Additional Information</h3>
                        <p className="mt-2 p-3 bg-muted rounded-md text-sm">{selectedConsultation.additionalInfo}</p>
                      </div>
                    )}

                    <div>
                      <h3 className="text-sm font-medium text-muted-foreground">Meeting Link</h3>
                      {selectedConsultation.meetingLink ? (
                        <div className="mt-2 p-3 bg-muted rounded-md text-sm break-all">
                          <a href={selectedConsultation.meetingLink} target="_blank" rel="noopener noreferrer" 
                             className="text-primary hover:underline">
                            {selectedConsultation.meetingLink}
                          </a>
                        </div>
                      ) : (
                        <p className="mt-2 p-3 bg-muted rounded-md text-sm text-muted-foreground">No meeting link added yet.</p>
                      )}
                    </div>

                    <div>
                      <h3 className="text-sm font-medium text-muted-foreground">Notes</h3>
                      {selectedConsultation.notes ? (
                        <p className="mt-2 p-3 bg-muted rounded-md text-sm">{selectedConsultation.notes}</p>
                      ) : (
                        <p className="mt-2 p-3 bg-muted rounded-md text-sm text-muted-foreground">No notes added yet.</p>
                      )}
                    </div>
                  </div>
                </div>
              ) : (
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium">Meeting Link</label>
                    <Input
                      value={meetingLink}
                      onChange={(e) => setMeetingLink(e.target.value)}
                      placeholder="e.g. https://zoom.us/j/123456789"
                      className="mt-1"
                    />
                  </div>

                  <div>
                    <label className="text-sm font-medium">Notes</label>
                    <Textarea
                      value={notes}
                      onChange={(e) => setNotes(e.target.value)}
                      placeholder="Add any notes or follow-up details here"
                      className="mt-1"
                      rows={5}
                    />
                  </div>
                </div>
              )}
            </div>

            <DialogFooter className="flex-col sm:flex-row gap-2">
              {viewMode === 'details' ? (
                <>
                  <div className="flex-1 flex gap-2">
                    <Button 
                      variant="destructive" 
                      onClick={handleDelete}
                      disabled={deleteConsultationMutation.isPending}
                    >
                      {deleteConsultationMutation.isPending ? (
                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      ) : (
                        <Trash className="h-4 w-4 mr-2" />
                      )}
                      Delete
                    </Button>
                    <Button
                      variant="outline"
                      onClick={handleSendReminder}
                      disabled={sendReminderMutation.isPending}
                    >
                      {sendReminderMutation.isPending ? (
                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      ) : (
                        <Mail className="h-4 w-4 mr-2" />
                      )}
                      Send Reminder
                    </Button>
                  </div>
                  <Button onClick={handleEditConsultation}>Edit Details</Button>
                </>
              ) : (
                <>
                  <Button 
                    variant="outline" 
                    onClick={() => setViewMode('details')}
                    disabled={updateDetailsMutation.isPending}
                  >
                    Cancel
                  </Button>
                  <Button 
                    onClick={handleSaveDetails}
                    disabled={updateDetailsMutation.isPending}
                  >
                    {updateDetailsMutation.isPending ? (
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    ) : (
                      <CheckCircle className="h-4 w-4 mr-2" />
                    )}
                    Save Changes
                  </Button>
                </>
              )}
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/components/admin/email-campai.content:
import React, { useState } from 'react';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Checkbox } from '@/components/ui/checkbox';
import { useMutation, useQuery } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { Loader2 } from 'lucide-react';

interface ContactData {
  id: number;
  name: string;
  email: string;
  phone: string | null;
  submittedAt: Date;
  selected?: boolean;
}

interface ServiceRequestData {
  id: number;
  name: string;
  email: string;
  phone: string | null;
  company: string | null;
  submittedAt: Date;
  selected?: boolean;
}

interface CampaignData {
  recipients: Array<{ name: string; email: string; phone?: string }>;
  subject: string;
  messageBody: string;
  campaignType: 'email' | 'sms';
}

const EmailCampaignManager: React.FC = () => {
  const { toast } = useToast();
  const [campaign, setCampaign] = useState<CampaignData>({
    recipients: [],
    subject: '',
    messageBody: '',
    campaignType: 'email'
  });

  // Fetch contacts and service requests
  const { data: contacts, isLoading: contactsLoading } = useQuery({
    queryKey: ['/api/admin/contacts'],
    queryFn: async () => {
      const res = await apiRequest('GET', '/api/contact-submissions');
      const data = await res.json();
      return data.map((item: ContactData) => ({ ...item, selected: false }));
    }
  });

  const { data: serviceRequests, isLoading: requestsLoading } = useQuery({
    queryKey: ['/api/admin/service-requests'],
    queryFn: async () => {
      const res = await apiRequest('GET', '/api/service-requests');
      const data = await res.json();
      return data.map((item: ServiceRequestData) => ({ ...item, selected: false }));
    }
  });

  // Track selected recipients
  const [selectedContacts, setSelectedContacts] = useState<ContactData[]>([]);
  const [selectedRequests, setSelectedRequests] = useState<ServiceRequestData[]>([]);

  const handleContactSelection = (contact: ContactData, selected: boolean) => {
    if (selected) {
      setSelectedContacts(prev => [...prev, contact]);
    } else {
      setSelectedContacts(prev => prev.filter(c => c.id !== contact.id));
    }
  };

  const handleRequestSelection = (request: ServiceRequestData, selected: boolean) => {
    if (selected) {
      setSelectedRequests(prev => [...prev, request]);
    } else {
      setSelectedRequests(prev => prev.filter(r => r.id !== request.id));
    }
  };

  const selectAllContacts = (selected: boolean) => {
    if (selected && contacts) {
      setSelectedContacts(contacts);
    } else {
      setSelectedContacts([]);
    }
  };

  const selectAllRequests = (selected: boolean) => {
    if (selected && serviceRequests) {
      setSelectedRequests(serviceRequests);
    } else {
      setSelectedRequests([]);
    }
  };

  // Prepare recipients for campaign
  const prepareRecipients = () => {
    const allRecipients = [
      ...selectedContacts.map(contact => ({
        name: contact.name,
        email: contact.email,
        phone: contact.phone || undefined
      })),
      ...selectedRequests.map(request => ({
        name: request.name,
        email: request.email,
        phone: request.phone || undefined
      }))
    ];

    // Remove duplicates based on email
    const uniqueRecipients = allRecipients.filter((recipient, index, self) => 
      index === self.findIndex(r => r.email === recipient.email)
    );

    setCampaign(prev => ({ ...prev, recipients: uniqueRecipients }));

    toast({
      title: "Recipients selected",
      description: `${uniqueRecipients.length} unique recipients added to campaign.`
    });
  };

  // Handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setCampaign(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Send campaign mutation
  const sendCampaignMutation = useMutation({
    mutationFn: async (campaignData: CampaignData) => {
      const endpoint = campaignData.campaignType === 'email' 
        ? '/api/admin/send-email-campaign' 
        : '/api/admin/send-sms-campaign';

      const res = await apiRequest('POST', endpoint, campaignData);
      return await res.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Campaign sent successfully",
        description: `${data.success} messages were sent successfully. ${data.failed} failed.`,
      });

      // Reset campaign form
      setCampaign({
        recipients: [],
        subject: '',
        messageBody: '',
        campaignType: 'email'
      });

      setSelectedContacts([]);
      setSelectedRequests([]);
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to send campaign",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Handle campaign type change
  const handleCampaignTypeChange = (type: 'email' | 'sms') => {
    setCampaign(prev => ({
      ...prev,
      campaignType: type
    }));
  };

  // Handle form submission
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (campaign.recipients.length === 0) {
      toast({
        title: "No recipients selected",
        description: "Please select at least one recipient for your campaign.",
        variant: "destructive"
      });
      return;
    }

    if (!campaign.messageBody) {
      toast({
        title: "Message is empty",
        description: "Please enter a message for your campaign.",
        variant: "destructive"
      });
      return;
    }

    if (campaign.campaignType === 'email' && !campaign.subject) {
      toast({
        title: "Subject is empty",
        description: "Please enter a subject for your email campaign.",
        variant: "destructive"
      });
      return;
    }

    // For SMS campaign, filter recipients who have a phone number
    const finalData = { ...campaign };
    if (campaign.campaignType === 'sms') {
      finalData.recipients = campaign.recipients.filter(r => r.phone);

      if (finalData.recipients.length === 0) {
        toast({
          title: "No valid phone numbers",
          description: "None of the selected recipients have a valid phone number.",
          variant: "destructive"
        });
        return;
      }
    }

    sendCampaignMutation.mutate(finalData);
  };

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle>Email & SMS Campaign Manager</CardTitle>
        <CardDescription>
          Create and send targeted email and SMS campaigns to your contacts and leads.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <Tabs defaultValue="recipients">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="recipients">Select Recipients</TabsTrigger>
            <TabsTrigger value="compose">Compose Message</TabsTrigger>
            <TabsTrigger value="preview">Preview & Send</TabsTrigger>
          </TabsList>

          <TabsContent value="recipients" className="space-y-4 mt-4">
            <Tabs defaultValue="contacts">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="contacts">Contact Form Submissions</TabsTrigger>
                <TabsTrigger value="requests">Service Requests</TabsTrigger>
              </TabsList>

              <TabsContent value="contacts" className="mt-4">
                {contactsLoading ? (
                  <div className="flex justify-center p-4">
                    <Loader2 className="h-8 w-8 animate-spin text-primary" />
                  </div>
                ) : contacts && contacts.length > 0 ? (
                  <div>
                    <div className="flex items-center mb-2">
                      <Checkbox 
                        id="selectAllContacts" 
                        checked={selectedContacts.length === contacts.length}
                        onCheckedChange={selectAllContacts}
                      />
                      <Label htmlFor="selectAllContacts" className="ml-2">Select All</Label>
                    </div>

                    <div className="rounded-md border">
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead className="w-12"></TableHead>
                            <TableHead>Name</TableHead>
                            <TableHead>Email</TableHead>
                            <TableHead>Phone</TableHead>
                            <TableHead>Date</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {contacts.map(contact => (
                            <TableRow key={contact.id}>
                              <TableCell>
                                <Checkbox 
                                  checked={selectedContacts.some(c => c.id === contact.id)}
                                  onCheckedChange={(checked) => 
                                    handleContactSelection(contact, checked as boolean)
                                  }
                                />
                              </TableCell>
                              <TableCell>{contact.name}</TableCell>
                              <TableCell>{contact.email}</TableCell>
                              <TableCell>{contact.phone || 'N/A'}</TableCell>
                              <TableCell>
                                {new Date(contact.submittedAt).toLocaleDateString()}
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </div>
                  </div>
                ) : (
                  <div className="text-center p-4 border rounded-md">
                    No contact form submissions found.
                  </div>
                )}
              </TabsContent>

              <TabsContent value="requests" className="mt-4">
                {requestsLoading ? (
                  <div className="flex justify-center p-4">
                    <Loader2 className="h-8 w-8 animate-spin text-primary" />
                  </div>
                ) : serviceRequests && serviceRequests.length > 0 ? (
                  <div>
                    <div className="flex items-center mb-2">
                      <Checkbox 
                        id="selectAllRequests" 
                        checked={selectedRequests.length === serviceRequests.length}
                        onCheckedChange={selectAllRequests}
                      />
                      <Label htmlFor="selectAllRequests" className="ml-2">Select All</Label>
                    </div>

                    <div className="rounded-md border">
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead className="w-12"></TableHead>
                            <TableHead>Name</TableHead>
                            <TableHead>Email</TableHead>
                            <TableHead>Phone</TableHead>
                            <TableHead>Company</TableHead>
                            <TableHead>Date</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {serviceRequests.map(request => (
                            <TableRow key={request.id}>
                              <TableCell>
                                <Checkbox 
                                  checked={selectedRequests.some(r => r.id === request.id)}
                                  onCheckedChange={(checked) => 
                                    handleRequestSelection(request, checked as boolean)
                                  }
                                />
                              </TableCell>
                              <TableCell>{request.name}</TableCell>
                              <TableCell>{request.email}</TableCell>
                              <TableCell>{request.phone || 'N/A'}</TableCell>
                              <TableCell>{request.company || 'N/A'}</TableCell>
                              <TableCell>
                                {new Date(request.submittedAt).toLocaleDateString()}
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </div>
                  </div>
                ) : (
                  <div className="text-center p-4 border rounded-md">
                    No service requests found.
                  </div>
                )}
              </TabsContent>
            </Tabs>

            <div className="flex justify-end mt-4">
              <Button onClick={prepareRecipients}>
                Use Selected Recipients ({selectedContacts.length + selectedRequests.length})
              </Button>
            </div>
          </TabsContent>

          <TabsContent value="compose" className="space-y-4 mt-4">
            <div className="space-y-4">
              <div className="flex space-x-2">
                <Button
                  variant={campaign.campaignType === 'email' ? 'default' : 'outline'}
                  onClick={() => handleCampaignTypeChange('email')}
                >
                  Email Campaign
                </Button>
                <Button
                  variant={campaign.campaignType === 'sms' ? 'default' : 'outline'}
                  onClick={() => handleCampaignTypeChange('sms')}
                >
                  SMS Campaign
                </Button>
              </div>

              {campaign.campaignType === 'email' && (
                <div className="space-y-2">
                  <Label htmlFor="subject">Email Subject</Label>
                  <Input
                    id="subject"
                    name="subject"
                    placeholder="Enter email subject line"
                    value={campaign.subject}
                    onChange={handleInputChange}
                  />
                </div>
              )}

              <div className="space-y-2">
                <Label htmlFor="messageBody">
                  {campaign.campaignType === 'email' ? 'Email Content' : 'SMS Message'}
                </Label>
                <Textarea
                  id="messageBody"
                  name="messageBody"
                  placeholder={campaign.campaignType === 'email' 
                    ? "Enter your email content. You can use {{name}} to personalize the message."
                    : "Enter your SMS message. You can use {{name}} to personalize the message. Keep it concise for SMS."
                  }
                  value={campaign.messageBody}
                  onChange={handleInputChange}
                  rows={campaign.campaignType === 'email' ? 10 : 4}
                />
                {campaign.campaignType === 'sms' && (
                  <p className="text-sm text-muted-foreground">
                    SMS messages should be kept short (under 160 characters for a single message).
                    Current length: {campaign.messageBody.length} characters
                  </p>
                )}
              </div>
            </div>
          </TabsContent>

          <TabsContent value="preview" className="space-y-4 mt-4">
            <div className="space-y-4">
              <div className="space-y-2">
                <h3 className="text-lg font-medium">Campaign Type</h3>
                <p>{campaign.campaignType === 'email' ? 'Email Campaign' : 'SMS Campaign'}</p>
              </div>

              <div className="space-y-2">
                <h3 className="text-lg font-medium">Recipients</h3>
                <p className="text-sm text-muted-foreground">
                  {campaign.recipients.length} recipient(s) selected
                </p>
                {campaign.recipients.length > 0 && (
                  <div className="max-h-40 overflow-y-auto rounded-md border p-2">
                    <ul className="space-y-1">
                      {campaign.recipients.map((recipient, index) => (
                        <li key={index} className="text-sm">
                          {recipient.name} ({recipient.email})
                          {campaign.campaignType === 'sms' && 
                            ` - ${recipient.phone ? recipient.phone : 'No phone number'}`
                          }
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>

              {campaign.campaignType === 'email' && (
                <div className="space-y-2">
                  <h3 className="text-lg font-medium">Subject</h3>
                  <p>{campaign.subject || 'No subject'}</p>
                </div>
              )}

              <div className="space-y-2">
                <h3 className="text-lg font-medium">Message Preview</h3>
                <div className="rounded-md border p-4 bg-gray-50 dark:bg-gray-900">
                  {campaign.messageBody ? (
                    <div className="whitespace-pre-wrap">
                      {campaign.messageBody.replace(/{{name}}/g, '[Recipient Name]')}
                    </div>
                  ) : (
                    <p className="text-muted-foreground">No message content</p>
                  )}
                </div>
              </div>
            </div>
          </TabsContent>
        </Tabs>
      </CardContent>
      <CardFooter className="flex justify-between">
        <div className="text-sm text-muted-foreground">
          {campaign.campaignType === 'email' 
            ? 'Emails will be sent via Brevo SMTP'
            : 'SMS messages will be sent via Twilio'
          }
        </div>
        <Button 
          onClick={handleSubmit}
          disabled={
            sendCampaignMutation.isPending || 
            campaign.recipients.length === 0 || 
            !campaign.messageBody || 
            (campaign.campaignType === 'email' && !campaign.subject)
          }
        >
          {sendCampaignMutation.isPending 
            ? 'Sending...' 
            : `Send ${campaign.campaignType === 'email' ? 'Email' : 'SMS'} Campaign`
          }
        </Button>
      </CardFooter>
    </Card>
  );
};

export default EmailCampaignManager;

client/src/components/admin/goal-trackin.content:
import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { 
  Card, 
  CardContent, 
  CardDescription, 
  CardHeader, 
  CardTitle,
  CardFooter
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { 
  Form, 
  FormControl, 
  FormDescription, 
  FormField, 
  FormItem, 
  FormLabel, 
  FormMessage 
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { 
  CheckCircle2, 
  Clock, 
  Edit, 
  Plus, 
  Target, 
  Trash, 
  TrendingUp,
  AlertCircle
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import { apiRequest, queryClient } from '@/lib/queryClient';

// Goal schema
const goalSchema = z.object({
  id: z.number().optional(),
  name: z.string().min(1, 'Goal name is required'),
  type: z.enum(['visits', 'conversions', 'rate', 'custom']),
  target: z.number().min(1, 'Target must be greater than 0'),
  current: z.number().default(0),
  period: z.enum(['daily', 'weekly', 'monthly', 'quarterly', 'yearly', 'custom']),
  startDate: z.string().optional(),
  endDate: z.string().optional(),
  trackerId: z.number().optional().nullable(),
  isActive: z.boolean().default(true),
});

type Goal = z.infer<typeof goalSchema>;
type NewGoal = Omit<Goal, 'id' | 'current'> & { id?: number, current?: number };

// Marketing activity schema
const activitySchema = z.object({
  id: z.number().optional(),
  name: z.string().min(1, 'Activity name is required'),
  type: z.enum(['campaign', 'social', 'email', 'content', 'other']),
  description: z.string().optional(),
  startDate: z.string(),
  endDate: z.string().optional(),
  status: z.enum(['planned', 'in_progress', 'completed', 'cancelled']),
  notes: z.string().optional(),
});

type MarketingActivity = z.infer<typeof activitySchema>;
type NewActivity = Omit<MarketingActivity, 'id'> & { id?: number };

const GoalTrackingDashboard: React.FC = () => {
  const [goalDialogOpen, setGoalDialogOpen] = useState(false);
  const [activityDialogOpen, setActivityDialogOpen] = useState(false);
  const [editGoal, setEditGoal] = useState<Goal | null>(null);
  const [editActivity, setEditActivity] = useState<MarketingActivity | null>(null);
  const { toast } = useToast();

  // Fetch goals
  const { data: goals, isLoading: isLoadingGoals } = useQuery<Goal[]>({
    queryKey: ['/api/goals'],
    queryFn: async () => {
      // If API endpoint doesn't exist yet, return empty array
      try {
        const res = await apiRequest('GET', '/api/goals');
        return await res.json();
      } catch (error) {
        console.error('Error fetching goals:', error);
        return [];
      }
    }
  });

  // Fetch marketing activities
  const { data: activities, isLoading: isLoadingActivities } = useQuery<MarketingActivity[]>({
    queryKey: ['/api/marketing-activities'],
    queryFn: async () => {
      // If API endpoint doesn't exist yet, return empty array
      try {
        const res = await apiRequest('GET', '/api/marketing-activities');
        return await res.json();
      } catch (error) {
        console.error('Error fetching activities:', error);
        return [];
      }
    }
  });

  // Fetch tracker list
  const { data: trackers } = useQuery<Array<{ id: number; name: string; platform: string }>>({
    queryKey: ['/api/ad-trackers'],
  });

  // Forms
  const goalForm = useForm<NewGoal>({
    resolver: zodResolver(goalSchema.omit({ id: true, current: true })),
    defaultValues: {
      name: '',
      type: 'visits',
      target: 100,
      period: 'monthly',
      trackerId: null,
      isActive: true
    }
  });

  const activityForm = useForm<NewActivity>({
    resolver: zodResolver(activitySchema.omit({ id: true })),
    defaultValues: {
      name: '',
      type: 'campaign',
      startDate: new Date().toISOString().split('T')[0],
      status: 'planned'
    }
  });

  // Mutations
  const createGoalMutation = useMutation({
    mutationFn: async (data: NewGoal) => {
      const res = await apiRequest('POST', '/api/goals', data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals'] });
      toast({
        title: 'Goal created',
        description: 'Marketing goal has been created successfully'
      });
      setGoalDialogOpen(false);
      goalForm.reset();
    },
    onError: (error) => {
      toast({
        title: 'Error creating goal',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  const updateGoalMutation = useMutation({
    mutationFn: async (data: Goal) => {
      const res = await apiRequest('PUT', `/api/goals/${data.id}`, data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals'] });
      toast({
        title: 'Goal updated',
        description: 'Marketing goal has been updated successfully'
      });
      setGoalDialogOpen(false);
      setEditGoal(null);
      goalForm.reset();
    },
    onError: (error) => {
      toast({
        title: 'Error updating goal',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  const deleteGoalMutation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest('DELETE', `/api/goals/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals'] });
      toast({
        title: 'Goal deleted',
        description: 'Marketing goal has been deleted successfully'
      });
    },
    onError: (error) => {
      toast({
        title: 'Error deleting goal',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  const createActivityMutation = useMutation({
    mutationFn: async (data: NewActivity) => {
      const res = await apiRequest('POST', '/api/marketing-activities', data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-activities'] });
      toast({
        title: 'Activity created',
        description: 'Marketing activity has been created successfully'
      });
      setActivityDialogOpen(false);
      activityForm.reset();
    },
    onError: (error) => {
      toast({
        title: 'Error creating activity',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  const updateActivityMutation = useMutation({
    mutationFn: async (data: MarketingActivity) => {
      const res = await apiRequest('PUT', `/api/marketing-activities/${data.id}`, data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-activities'] });
      toast({
        title: 'Activity updated',
        description: 'Marketing activity has been updated successfully'
      });
      setActivityDialogOpen(false);
      setEditActivity(null);
      activityForm.reset();
    },
    onError: (error) => {
      toast({
        title: 'Error updating activity',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  const deleteActivityMutation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest('DELETE', `/api/marketing-activities/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-activities'] });
      toast({
        title: 'Activity deleted',
        description: 'Marketing activity has been deleted successfully'
      });
    },
    onError: (error) => {
      toast({
        title: 'Error deleting activity',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  // Handle form submissions
  const onGoalSubmit = (data: NewGoal) => {
    if (editGoal) {
      updateGoalMutation.mutate({ ...data, id: editGoal.id, current: editGoal.current });
    } else {
      createGoalMutation.mutate(data);
    }
  };

  const onActivitySubmit = (data: NewActivity) => {
    if (editActivity) {
      updateActivityMutation.mutate({ ...data, id: editActivity.id });
    } else {
      createActivityMutation.mutate(data);
    }
  };

  // Setup edit mode
  const handleEditGoal = (goal: Goal) => {
    setEditGoal(goal);
    goalForm.reset({
      name: goal.name,
      type: goal.type,
      target: goal.target,
      period: goal.period,
      startDate: goal.startDate,
      endDate: goal.endDate,
      trackerId: goal.trackerId,
      isActive: goal.isActive
    });
    setGoalDialogOpen(true);
  };

  const handleEditActivity = (activity: MarketingActivity) => {
    setEditActivity(activity);
    activityForm.reset({
      name: activity.name,
      type: activity.type,
      description: activity.description,
      startDate: activity.startDate,
      endDate: activity.endDate,
      status: activity.status,
      notes: activity.notes
    });
    setActivityDialogOpen(true);
  };

  // Calculate progress percentage
  const calculateProgress = (current: number, target: number) => {
    const progress = (current / target) * 100;
    return Math.min(Math.max(progress, 0), 100);
  };

  // Render status badge
  const renderStatusBadge = (status: MarketingActivity['status']) => {
    const statusConfig = {
      planned: { color: 'bg-blue-100 text-blue-800', icon: <Clock className="h-3 w-3 mr-1" /> },
      in_progress: { color: 'bg-yellow-100 text-yellow-800', icon: <TrendingUp className="h-3 w-3 mr-1" /> },
      completed: { color: 'bg-green-100 text-green-800', icon: <CheckCircle2 className="h-3 w-3 mr-1" /> },
      cancelled: { color: 'bg-red-100 text-red-800', icon: <AlertCircle className="h-3 w-3 mr-1" /> }
    };

    const { color, icon } = statusConfig[status];

    return (
      <span className={`text-xs font-medium px-2 py-1 rounded-full flex items-center w-fit ${color}`}>
        {icon}
        {status.replace('_', ' ')}
      </span>
    );
  };

  // Reset forms when closing dialogs
  const handleGoalDialogClose = () => {
    setGoalDialogOpen(false);
    setEditGoal(null);
    goalForm.reset();
  };

  const handleActivityDialogClose = () => {
    setActivityDialogOpen(false);
    setEditActivity(null);
    activityForm.reset();
  };

  return (
    <div className="space-y-8">
      {/* Goals Section */}
      <div>
        <div className="flex justify-between items-center mb-4">
          <div>
            <h2 className="text-2xl font-bold">Marketing Goals</h2>
            <p className="text-muted-foreground">Set and track goals for your marketing campaigns</p>
          </div>
          <Dialog open={goalDialogOpen} onOpenChange={setGoalDialogOpen}>
            <DialogTrigger asChild>
              <Button onClick={() => {
                setEditGoal(null);
                goalForm.reset({
                  name: '',
                  type: 'visits',
                  target: 100,
                  period: 'monthly',
                  trackerId: null,
                  isActive: true
                });
              }}>
                <Plus className="mr-2 h-4 w-4" />
                Add Goal
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[500px]">
              <DialogHeader>
                <DialogTitle>{editGoal ? 'Edit Goal' : 'Create New Goal'}</DialogTitle>
              </DialogHeader>
              <Form {...goalForm}>
                <form onSubmit={goalForm.handleSubmit(onGoalSubmit)} className="space-y-4">
                  <FormField
                    control={goalForm.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name</FormLabel>
                        <FormControl>
                          <Input placeholder="Goal name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={goalForm.control}
                      name="type"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Type</FormLabel>
                          <Select 
                            onValueChange={field.onChange} 
                            defaultValue={field.value}
                          >
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select goal type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="visits">Visits</SelectItem>
                              <SelectItem value="conversions">Conversions</SelectItem>
                              <SelectItem value="rate">Conversion Rate</SelectItem>
                              <SelectItem value="custom">Custom</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={goalForm.control}
                      name="target"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Target</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              {...field} 
                              onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={goalForm.control}
                      name="period"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Period</FormLabel>
                          <Select 
                            onValueChange={field.onChange} 
                            defaultValue={field.value}
                          >
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select period" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="daily">Daily</SelectItem>
                              <SelectItem value="weekly">Weekly</SelectItem>
                              <SelectItem value="monthly">Monthly</SelectItem>
                              <SelectItem value="quarterly">Quarterly</SelectItem>
                              <SelectItem value="yearly">Yearly</SelectItem>
                              <SelectItem value="custom">Custom Date Range</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={goalForm.control}
                      name="trackerId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Campaign</FormLabel>
                          <Select 
