  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { DatePicker } from "@/components/ui/date-picker";
import { apiRequest, queryClient } from "@/lib/queryClient";
import {
  Table,
  TableBody,
  TableCaption,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "@/components/ui/tabs";

const activitySchema = z.object({
  name: z.string().min(3, "Activity name must be at least 3 characters long"),
  description: z.string().optional(),
  type: z.string(),
  budget: z.coerce.number().min(0, "Budget cannot be negative").optional(),
  startDate: z.date(),
  endDate: z.date(),
  status: z.string().default("planned"),
  platform: z.string().optional(),
  channels: z.array(z.string()).default([]),
  goalId: z.coerce.number().optional(),
  metrics: z.record(z.string(), z.any()).optional(),
  notes: z.string().optional(),
});

type ActivityFormValues = z.infer<typeof activitySchema>;

const activityTypes = {
  "social-campaign": "Social Media Campaign",
  "email-campaign": "Email Campaign",
  "content-campaign": "Content Campaign",
  "digital-ads": "Digital Advertising",
  "seo-initiative": "SEO Initiative",
  "event": "Event/Webinar",
  "partnership": "Partnership/Collaboration",
  "influencer": "Influencer Marketing",
  "pr-campaign": "PR Campaign",
  "other": "Other"
};

const platforms = {
  "facebook": "Facebook",
  "instagram": "Instagram", 
  "twitter": "Twitter/X",
  "linkedin": "LinkedIn",
  "tiktok": "TikTok",
  "youtube": "YouTube",
  "pinterest": "Pinterest",
  "google": "Google",
  "bing": "Bing/Microsoft",
  "email": "Email",
  "website": "Website",
  "blog": "Blog",
  "other": "Other"
};

const channels = [
  "social-media",
  "email",
  "search-engine",
  "display-ads",
  "content-marketing",
  "influencer",
  "video",
  "affiliate",
  "referral",
  "direct-mail",
  "events",
  "webinars",
  "podcasts"
];

function formatDate(date: Date | string): string {
  if (typeof date === 'string') {
    date = new Date(date);
  }
  return new Intl.DateTimeFormat('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
  }).format(date);
}

function getStatusBadge(status: string) {
  switch (status) {
    case "completed":
      return <Badge className="bg-green-500">Completed</Badge>;
    case "active":
      return <Badge className="bg-blue-500">Active</Badge>;
    case "planned":
      return <Badge className="bg-amber-500">Planned</Badge>;
    case "cancelled":
      return <Badge className="bg-red-500">Cancelled</Badge>;
    case "paused":
      return <Badge className="bg-gray-500">Paused</Badge>;
    default:
      return <Badge>{status}</Badge>;
  }
}

function ActivityCard({ activity, onEdit, onDelete }: { activity: any; onEdit: (activity: any) => void; onDelete: (id: number) => void }) {
  return (
    <Card className="mb-4">
      <CardHeader className="pb-2">
        <div className="flex justify-between items-start">
          <div>
            <CardTitle className="text-xl mb-1">{activity.name}</CardTitle>
            <CardDescription className="text-sm text-gray-600">
              {activityTypes[activity.type as keyof typeof activityTypes]}
            </CardDescription>
          </div>
          <div>
            {getStatusBadge(activity.status)}
          </div>
        </div>
      </CardHeader>
      <CardContent className="py-2">
        {activity.description && (
          <p className="text-sm mb-3 text-gray-700">{activity.description}</p>
        )}
        <div className="flex justify-between text-sm text-gray-500 mb-3">
          <div className="flex items-center">
            <Calendar className="h-4 w-4 mr-1" />
            <span>{formatDate(activity.startDate)} - {formatDate(activity.endDate)}</span>
          </div>
          {activity.platform && (
            <div className="flex items-center">
              <Megaphone className="h-4 w-4 mr-1" />
              <span>{platforms[activity.platform as keyof typeof platforms]}</span>
            </div>
          )}
        </div>
        {activity.budget && (
          <div className="flex items-center text-sm font-medium mb-2">
            <span>Budget: ${activity.budget.toLocaleString()}</span>
          </div>
        )}
      </CardContent>
      <CardFooter className="pt-2 flex justify-end space-x-2">
        <Button 
          variant="outline" 
          size="sm" 
          onClick={() => onEdit(activity)}
        >
          <Edit className="h-4 w-4 mr-1" /> Edit
        </Button>
        <Button 
          variant="destructive" 
          size="sm" 
          onClick={() => onDelete(activity.id)}
        >
          <Trash2 className="h-4 w-4 mr-1" /> Delete
        </Button>
      </CardFooter>
    </Card>
  );
}

export default function MarketingActivities() {
