import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  Loader2,
  PlusCircle,
  Trash2,
  Edit,
  Check,
  XCircle,
  BarChart3,
  Target,
  Calendar,
  TrendingUp,
  Sparkles,
  RefreshCw,
} from "lucide-react";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Card,
  CardHeader,
  CardTitle,
  CardDescription,
  CardContent,
  CardFooter,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { DatePicker } from "@/components/ui/date-picker";

const goalSchema = z.object({
  name: z.string().min(3, "Goal name must be at least 3 characters long"),
  description: z.string().optional(),
  targetValue: z.coerce.number().positive("Target must be a positive number"),
  currentValue: z.coerce.number().min(0, "Current value cannot be negative").default(0),
  category: z.string(),
  startDate: z.date(),
  targetDate: z.date(),
  status: z.string().default("active"),
  priority: z.string().default("medium"),
});

type GoalFormValues = z.infer<typeof goalSchema>;

function formatDate(date: Date | string): string {
  if (typeof date === 'string') {
    date = new Date(date);
  }
  return new Intl.DateTimeFormat('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
  }).format(date);
}

function calculateProgress(current: number, target: number): number {
  if (target === 0) return 0; // Avoid division by zero
  return Math.min(Math.round((current / target) * 100), 100);
}

function getStatusColor(status: string): string {
  switch (status) {
    case "completed":
      return "bg-green-500";
    case "active":
      return "bg-blue-500";
    case "at-risk":
      return "bg-amber-500";
    case "behind":
      return "bg-red-500";
    case "on-hold":
      return "bg-gray-500";
    default:
      return "bg-blue-500";
  }
}

function getPriorityBadge(priority: string) {
  switch (priority) {
    case "high":
      return <Badge className="bg-red-500">High</Badge>;
    case "medium":
      return <Badge className="bg-amber-500">Medium</Badge>;
    case "low":
      return <Badge className="bg-green-500">Low</Badge>;
    default:
      return <Badge>Medium</Badge>;
  }
}

function GoalCard({ goal, onEdit, onDelete }: { goal: any; onEdit: (goal: any) => void; onDelete: (id: number) => void }) {
  const progress = calculateProgress(goal.currentValue, goal.targetValue);

  return (
    <Card className="mb-4">
      <CardHeader className="pb-2">
        <div className="flex justify-between items-start">
          <div>
            <CardTitle className="text-xl mb-1">{goal.name}</CardTitle>
            <CardDescription className="text-sm">{goal.description}</CardDescription>
          </div>
          <div className="flex space-x-2">
            {getPriorityBadge(goal.priority)}
            <Badge className={getStatusColor(goal.status)}>{goal.status}</Badge>
          </div>
        </div>
      </CardHeader>
      <CardContent className="py-2">
        <div className="mb-3">
          <div className="flex justify-between text-sm font-medium mb-1">
            <span>Progress: {goal.currentValue} / {goal.targetValue} ({progress}%)</span>
            <span>{goal.category}</span>
          </div>
          <Progress value={progress} />
        </div>
        <div className="flex justify-between text-sm text-gray-500 mb-2">
          <div className="flex items-center">
            <Calendar className="h-4 w-4 mr-1" />
            <span>Start: {formatDate(goal.startDate)}</span>
          </div>
          <div className="flex items-center">
            <Target className="h-4 w-4 mr-1" />
            <span>Target: {formatDate(goal.targetDate)}</span>
          </div>
        </div>
      </CardContent>
      <CardFooter className="pt-2 flex justify-end space-x-2">
        <Button 
          variant="outline" 
          size="sm" 
          onClick={() => onEdit(goal)}
        >
          <Edit className="h-4 w-4 mr-1" /> Update
        </Button>
        <Button 
          variant="destructive" 
          size="sm" 
          onClick={() => onDelete(goal.id)}
        >
          <Trash2 className="h-4 w-4 mr-1" /> Delete
        </Button>
      </CardFooter>
    </Card>
  );
}

