    defaultValues: {
      name: "",
      description: "",
      targetValue: 0,
      currentValue: 0,
      category: "leads",
      status: "active",
      priority: "medium",
      startDate: new Date(),
      targetDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days from now
    },
  });

  const updateForm = useForm<GoalFormValues>({
    resolver: zodResolver(goalSchema),
    defaultValues: {
      name: "",
      description: "",
