        const matchesSearch =
          searchQuery === "" ||
          item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          (item.alt &&
            item.alt.toLowerCase().includes(searchQuery.toLowerCase())) ||
          (item.tags &&
            item.tags.some((tag: string) =>
              tag.toLowerCase().includes(searchQuery.toLowerCase())
            ));

        const matchesTag =
          !filterTag || (item.tags && item.tags.includes(filterTag));

        return matchesSearch && matchesTag;
      })
    : [];

  // Sort the filtered media items
  filteredMediaItems = [...filteredMediaItems].sort((a: any, b: any) => {
    switch (sortBy) {
      case "newest":
        return (
          new Date(b.uploadedAt).getTime() - new Date(a.uploadedAt).getTime()
        );
      case "oldest":
        return (
          new Date(a.uploadedAt).getTime() - new Date(b.uploadedAt).getTime()
        );
      case "name":
        return a.name.localeCompare(b.name);
      case "size":
        return b.size - a.size;
      default:
        return 0;
    }
  });

  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-3xl font-bold tracking-tight">Media Library</h2>
          <p className="text-muted-foreground">
            Manage all your website images and media assets
          </p>
        </div>
        <div className="flex gap-2">
          <input
            type="file"
            ref={fileInputRef}
            className="hidden"
            accept="image/*"
            onChange={handleFileUpload}
          />
          <Button
            variant="outline"
            onClick={() => {
              setUploadMethod("url");
              setUploadUrl("");
              setUploadName("");
              setUploadAlt("");
              setUploadTags("");
              setIsUploadModalOpen(true);
            }}
          >
            <ExternalLink className="mr-2 h-4 w-4" />
            Add from URL
          </Button>
          <Button onClick={() => fileInputRef.current?.click()}>
            <Upload className="mr-2 h-4 w-4" />
            Upload
          </Button>
        </div>
      </div>

      {/* Filters and View Controls */}
      <div className="flex flex-col md:flex-row justify-between mb-4 space-y-3 md:space-y-0">
        <div className="flex flex-wrap gap-2 items-center">
          <div className="relative w-60">
            <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-gray-500" />
            <Input
              type="search"
              placeholder="Search media..."
              className="pl-8"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          <Select
            value={filterTag || ""}
            onValueChange={(value) => setFilterTag(value || null)}
          >
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Filter by tag" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="">All Tags</SelectItem>
              {allTags.map((tag) => (
                <SelectItem key={tag} value={tag}>
                  {tag}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Select value={sortBy} onValueChange={(value: any) => setSortBy(value)}>
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="Sort by" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="newest">Newest</SelectItem>
              <SelectItem value="oldest">Oldest</SelectItem>
              <SelectItem value="name">Name</SelectItem>
              <SelectItem value="size">Size</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div className="flex items-center space-x-2">
          <Button
            variant={viewMode === "grid" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("grid")}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              width="16"
              height="16"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="mr-1"
            >
              <rect width="7" height="7" x="3" y="3" rx="1" />
              <rect width="7" height="7" x="14" y="3" rx="1" />
              <rect width="7" height="7" x="14" y="14" rx="1" />
              <rect width="7" height="7" x="3" y="14" rx="1" />
            </svg>
            Grid
          </Button>
          <Button
            variant={viewMode === "list" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("list")}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              width="16"
              height="16"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="mr-1"
            >
              <line x1="3" x2="21" y1="6" y2="6" />
              <line x1="3" x2="21" y1="12" y2="12" />
              <line x1="3" x2="21" y1="18" y2="18" />
            </svg>
            List
          </Button>
        </div>
      </div>

      {/* Media Content */}
      {isLoading ? (
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {Array(8)
            .fill(0)
            .map((_, index) => (
              <div key={index} className="space-y-2">
                <Skeleton className="aspect-square rounded-md" />
                <Skeleton className="h-4 w-full" />
              </div>
            ))}
        </div>
      ) : error ? (
        <Alert variant="destructive" className="mb-6">
          <AlertTitle>Error loading media</AlertTitle>
          <AlertDescription>
            {(error as Error).message ||
              "An error occurred while loading your media library."}
          </AlertDescription>
        </Alert>
      ) : filteredMediaItems.length === 0 ? (
        <div className="text-center py-12 border-2 border-dashed border-gray-200 rounded-md">
          <FileImage className="h-12 w-12 mx-auto text-gray-400 mb-3" />
          <h3 className="text-lg font-medium mb-2">No media found</h3>
          <p className="text-gray-500 mb-4">
            {searchQuery || filterTag
              ? "No matches found. Try different search terms or filters."
              : "Your media library is empty. Upload images to get started."}
          </p>
          {(searchQuery || filterTag) && (
            <Button
              variant="outline"
              onClick={() => {
                setSearchQuery("");
                setFilterTag(null);
              }}
            >
              Clear Filters
            </Button>
          )}
        </div>
      ) : viewMode === "grid" ? (
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {filteredMediaItems.map((item: any) => (
            <div
              key={item.id}
              className="border rounded-md overflow-hidden group hover:shadow-md transition-shadow duration-200"
            >
              <div
                className="aspect-square bg-gray-100 relative cursor-pointer"
                onClick={() => handleViewDetails(item)}
              >
                <img
                  src={item.url}
                  alt={item.alt || item.name}
                  className="w-full h-full object-contain p-2"
                  onError={(e) => {
                    (e.target as HTMLImageElement).src =
                      "https://placehold.co/400?text=Error";
                  }}
                />
                <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex items-center justify-center gap-2">
                  <Button
                    size="sm"
                    variant="secondary"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleCopyUrl(item.url);
                    }}
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <Button
                    size="sm"
                    variant="secondary"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEditClick(item);
                    }}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                </div>
              </div>
              <div className="p-2">
                <div className="text-sm font-medium truncate">{item.name}</div>
                <div className="flex justify-between items-center mt-1">
                  <div className="text-xs text-gray-500">
                    {formatFileSize(item.size)}
                  </div>
                  {item.tags && item.tags.length > 0 && (
                    <div className="flex gap-1">
                      {item.tags.slice(0, 2).map((tag: string) => (
                        <Badge
                          key={tag}
                          variant="outline"
                          className="text-xs py-0 px-1.5"
                        >
                          {tag}
                        </Badge>
                      ))}
                      {item.tags.length > 2 && (
                        <Badge
                          variant="outline"
                          className="text-xs py-0 px-1.5"
                        >
                          +{item.tags.length - 2}
                        </Badge>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="border rounded-md">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="w-[80px]">Preview</TableHead>
                <TableHead>Name</TableHead>
                <TableHead className="hidden md:table-cell">Type</TableHead>
                <TableHead className="hidden md:table-cell">Size</TableHead>
                <TableHead className="hidden md:table-cell">Dimensions</TableHead>
                <TableHead className="hidden md:table-cell">Date</TableHead>
                <TableHead className="hidden md:table-cell">Tags</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredMediaItems.map((item: any) => (
                <TableRow key={item.id}>
                  <TableCell className="p-2">
                    <div
                      className="w-16 h-16 bg-gray-100 rounded flex items-center justify-center overflow-hidden cursor-pointer"
                      onClick={() => handleViewDetails(item)}
                    >
                      <img
                        src={item.url}
                        alt={item.alt || item.name}
                        className="max-w-full max-h-full object-contain"
                        onError={(e) => {
                          (e.target as HTMLImageElement).src =
                            "https://placehold.co/400?text=Error";
                        }}
                      />
                    </div>
                  </TableCell>
                  <TableCell className="font-medium">{item.name}</TableCell>
                  <TableCell className="hidden md:table-cell">
                    {item.type.split("/")[1].toUpperCase()}
                  </TableCell>
                  <TableCell className="hidden md:table-cell">
                    {formatFileSize(item.size)}
                  </TableCell>
                  <TableCell className="hidden md:table-cell">
                    {item.dimensions
                      ? `${item.dimensions.width} × ${item.dimensions.height}`
                      : "—"}
                  </TableCell>
                  <TableCell className="hidden md:table-cell">
                    {formatDate(item.uploadedAt)}
                  </TableCell>
                  <TableCell className="hidden md:table-cell">
                    {item.tags && item.tags.length > 0 ? (
                      <div className="flex flex-wrap gap-1">
                        {item.tags.map((tag: string) => (
                          <Badge
                            key={tag}
                            variant="outline"
                            className="text-xs py-0 px-1.5"
                          >
                            {tag}
                          </Badge>
                        ))}
                      </div>
                    ) : (
                      "—"
                    )}
                  </TableCell>
                  <TableCell className="text-right">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="sm">
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="24"
                            height="24"
                            viewBox="0 0 24 24"
                            fill="none"
                            stroke="currentColor"
                            strokeWidth="2"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            className="lucide lucide-more-vertical"
                          >
                            <circle cx="12" cy="12" r="1" />
                            <circle cx="12" cy="5" r="1" />
                            <circle cx="12" cy="19" r="1" />
                          </svg>
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuLabel>Actions</DropdownMenuLabel>
                        <DropdownMenuItem
                          onClick={() => handleViewDetails(item)}
                        >
                          <Eye className="mr-2 h-4 w-4" />
                          View Details
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleCopyUrl(item.url)}>
                          <Copy className="mr-2 h-4 w-4" />
                          Copy URL
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleEditClick(item)}>
                          <Edit className="mr-2 h-4 w-4" />
                          Edit Details
                        </DropdownMenuItem>
                        <DropdownMenuSeparator />
                        <DropdownMenuItem
                          className="text-red-600"
                          onClick={() => {
                            setSelectedItem(item);
                            setIsDeleteConfirmOpen(true);
                          }}
                        >
                          <Trash2 className="mr-2 h-4 w-4" />
                          Delete
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}

      {/* Upload Modal */}
      <Dialog open={isUploadModalOpen} onOpenChange={setIsUploadModalOpen}>
        <DialogContent className="sm:max-w-[550px]">
          <DialogHeader>
            <DialogTitle>Upload Media</DialogTitle>
            <DialogDescription>
              Add new images to your media library
            </DialogDescription>
          </DialogHeader>

          <Tabs
            defaultValue={uploadMethod}
            value={uploadMethod}
            onValueChange={(v: any) => setUploadMethod(v)}
          >
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="file">Upload File</TabsTrigger>
              <TabsTrigger value="url">From URL</TabsTrigger>
            </TabsList>

            <TabsContent value="file" className="space-y-4 pt-4">
              <div className="border-2 border-dashed rounded-md p-6 text-center">
                <input
                  type="file"
                  ref={fileInputRef}
                  className="hidden"
                  accept="image/*"
                  onChange={handleFileUpload}
                />
                <Button
                  variant="outline"
                  className="w-full h-32 flex flex-col items-center justify-center"
                  onClick={() => fileInputRef.current?.click()}
                >
                  <FileUp className="h-8 w-8 mb-2 text-gray-400" />
                  <div className="font-medium">Click to select a file</div>
                  <div className="text-xs text-gray-500 mt-1">
                    or drag and drop
                  </div>
                </Button>
              </div>

              {fileInputRef.current?.files?.length ? (
                <div className="p-3 border rounded-md bg-gray-50 flex items-center gap-3">
                  <div className="w-12 h-12 bg-gray-100 rounded overflow-hidden flex items-center justify-center">
                    <img
                      src={URL.createObjectURL(fileInputRef.current.files[0])}
                      alt="Preview"
                      className="max-w-full max-h-full object-contain"
                    />
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="font-medium truncate">
                      {fileInputRef.current.files[0].name}
                    </div>
                    <div className="text-xs text-gray-500">
                      {formatFileSize(fileInputRef.current.files[0].size)}
                    </div>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => {
                      if (fileInputRef.current) {
                        fileInputRef.current.value = "";
                      }
                    }}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              ) : null}
            </TabsContent>

            <TabsContent value="url" className="space-y-4 pt-4">
              <div>
                <Label htmlFor="image-url">Image URL</Label>
                <Input
                  id="image-url"
                  placeholder="https://example.com/image.jpg"
                  value={uploadUrl}
                  onChange={(e) => setUploadUrl(e.target.value)}
                />
              </div>

              {uploadUrl && (
                <div className="p-3 border rounded-md bg-gray-50">
                  <div className="aspect-video relative bg-gray-100 rounded-md overflow-hidden">
                    <img
                      src={uploadUrl}
                      alt="Preview"
                      className="absolute inset-0 w-full h-full object-contain"
                      onError={(e) => {
                        (e.target as HTMLImageElement).src =
                          "https://placehold.co/400x300?text=Invalid+Image+URL";
                      }}
                    />
                  </div>
                </div>
              )}
            </TabsContent>
          </Tabs>

          <div className="space-y-4 pt-2">
            <div>
              <Label htmlFor="media-name">Name</Label>
              <Input
                id="media-name"
                placeholder="My Image"
                value={uploadName}
                onChange={(e) => setUploadName(e.target.value)}
              />
            </div>

            <div>
              <Label htmlFor="media-alt">Alt Text</Label>
              <Input
                id="media-alt"
                placeholder="Descriptive text for accessibility"
                value={uploadAlt}
                onChange={(e) => setUploadAlt(e.target.value)}
              />
              <div className="text-xs text-gray-500 mt-1">
                Describe the image for screen readers and SEO
              </div>
            </div>

            <div>
              <Label htmlFor="media-tags">Tags</Label>
              <Input
                id="media-tags"
                placeholder="logo, banner, product (comma separated)"
                value={uploadTags}
                onChange={(e) => setUploadTags(e.target.value)}
              />
            </div>
          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => setIsUploadModalOpen(false)}>
              Cancel
            </Button>
            <Button
              onClick={handleUploadSubmit}
              disabled={isUploading}
            >
              {isUploading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              {isUploading ? "Uploading..." : "Upload"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Modal */}
      <Dialog open={isEditModalOpen} onOpenChange={setIsEditModalOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit Media</DialogTitle>
            <DialogDescription>Update media details</DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            {selectedItem && (
              <div className="p-3 border rounded-md bg-gray-50 flex items-center gap-3">
                <div className="w-14 h-14 bg-gray-100 rounded overflow-hidden flex items-center justify-center">
                  <img
                    src={selectedItem.url}
                    alt={selectedItem.alt || selectedItem.name}
                    className="max-w-full max-h-full object-contain"
                    onError={(e) => {
                      (e.target as HTMLImageElement).src =
                        "https://placehold.co/400?text=Error";
                    }}
                  />
                </div>
                <div className="flex-1 min-w-0">
                  <div className="font-medium truncate">
                    {selectedItem.type.split("/")[1].toUpperCase()} Image
                  </div>
                  <div className="text-xs text-gray-500">
                    {formatFileSize(selectedItem.size)} •{" "}
                    {selectedItem.dimensions
                      ? `${selectedItem.dimensions.width} × ${selectedItem.dimensions.height}`
                      : "Unknown dimensions"}
                  </div>
                </div>
              </div>
            )}

            <div>
              <Label htmlFor="edit-name">Name</Label>
              <Input
                id="edit-name"
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
              />
            </div>

            <div>
              <Label htmlFor="edit-alt">Alt Text</Label>
              <Input
                id="edit-alt"
                value={editAlt}
                onChange={(e) => setEditAlt(e.target.value)}
              />
              <div className="text-xs text-gray-500 mt-1">
                Describe the image for screen readers and SEO
              </div>
            </div>

            <div>
              <Label htmlFor="edit-tags">Tags</Label>
              <Input
                id="edit-tags"
                placeholder="logo, banner, product (comma separated)"
                value={editTags}
                onChange={(e) => setEditTags(e.target.value)}
              />
            </div>
          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditModalOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleEditSubmit}>Save Changes</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Details Modal */}
      <Dialog open={isDetailModalOpen} onOpenChange={setIsDetailModalOpen}>
        <DialogContent className="sm:max-w-[600px]">
          <DialogHeader>
            <DialogTitle>Media Details</DialogTitle>
          </DialogHeader>

          {selectedItem && (
            <div className="space-y-4">
              <div className="relative aspect-video bg-gray-100 rounded-md overflow-hidden">
                <img
                  src={selectedItem.url}
                  alt={selectedItem.alt || selectedItem.name}
                  className="absolute inset-0 w-full h-full object-contain"
                  onError={(e) => {
                    (e.target as HTMLImageElement).src =
                      "https://placehold.co/800x450?text=Error+Loading+Image";
                  }}
                />
              </div>

              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <h3 className="text-sm font-medium text-gray-500">Name</h3>
                  <p className="mt-1">{selectedItem.name}</p>
                </div>

                <div>
                  <h3 className="text-sm font-medium text-gray-500">Type</h3>
                  <p className="mt-1">{selectedItem.type}</p>
                </div>

                <div>
                  <h3 className="text-sm font-medium text-gray-500">
                    Dimensions
                  </h3>
                  <p className="mt-1">
                    {selectedItem.dimensions
                      ? `${selectedItem.dimensions.width} × ${selectedItem.dimensions.height} pixels`
                      : "Unknown"}
                  </p>
                </div>

                <div>
                  <h3 className="text-sm font-medium text-gray-500">Size</h3>
                  <p className="mt-1">{formatFileSize(selectedItem.size)}</p>
                </div>

                <div>
                  <h3 className="text-sm font-medium text-gray-500">
                    Upload Date
                  </h3>
                  <p className="mt-1">{formatDate(selectedItem.uploadedAt)}</p>
                </div>

                <div>
                  <h3 className="text-sm font-medium text-gray-500">Alt Text</h3>
                  <p className="mt-1">{selectedItem.alt || "—"}</p>
                </div>

                <div className="md:col-span-2">
                  <h3 className="text-sm font-medium text-gray-500">URL</h3>
                  <div className="mt-1 flex items-center gap-2">
                    <code className="text-xs bg-gray-100 p-2 rounded flex-1 overflow-x-auto">
                      {selectedItem.url}
                    </code>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleCopyUrl(selectedItem.url)}
                    >
                      <Copy className="h-4 w-4" />
                    </Button>
                  </div>
                </div>

                <div className="md:col-span-2">
                  <h3 className="text-sm font-medium text-gray-500">Tags</h3>
                  <div className="mt-1">
                    {selectedItem.tags && selectedItem.tags.length > 0 ? (
                      <div className="flex flex-wrap gap-1">
                        {selectedItem.tags.map((tag: string) => (
                          <Badge key={tag} variant="secondary">
                            {tag}
                          </Badge>
                        ))}
                      </div>
                    ) : (
                      <p className="text-gray-500">No tags</p>
                    )}
                  </div>
                </div>
              </div>

              <div className="flex justify-between pt-4">
                <Button
                  variant="outline"
                  onClick={() => handleEditClick(selectedItem)}
                >
                  <Edit className="mr-2 h-4 w-4" /> Edit Details
                </Button>
                <AlertDialog open={isDeleteConfirmOpen} onOpenChange={setIsDeleteConfirmOpen}>
                  <AlertDialogTrigger asChild>
                    <Button variant="destructive">
                      <Trash2 className="mr-2 h-4 w-4" /> Delete Media
                    </Button>
                  </AlertDialogTrigger>
                  <AlertDialogContent>
                    <AlertDialogHeader>
                      <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                      <AlertDialogDescription>
                        This will permanently delete this media from your
                        library. This action cannot be undone.
                      </AlertDialogDescription>
                    </AlertDialogHeader>
                    <AlertDialogFooter>
                      <AlertDialogCancel>Cancel</AlertDialogCancel>
                      <AlertDialogAction onClick={handleDeleteMedia}>
                        Delete
                      </AlertDialogAction>
                    </AlertDialogFooter>
                  </AlertDialogContent>
                </AlertDialog>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

