export default function ProjectRoadmap() {
  const roadmapRef = useRef(null);
  const isInView = useInView(roadmapRef, { once: false, amount: 0.2 });
  const controls = useAnimation();

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  return (
    <section className="py-16 bg-gradient-to-b from-gray-50 to-white dark:from-gray-950 dark:to-gray-900">
      <div className="container mx-auto px-4">
        <div className="max-w-3xl mx-auto text-center mb-10">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-4">
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-primary to-purple-600">
              Project Roadmap
            </span>
          </h2>
          <p className="text-gray-600 dark:text-gray-300">
            A clear, step-by-step process ensures your project is delivered on time and to specification.
          </p>
        </div>

        <div 
          ref={roadmapRef}
          className="relative mt-20 pb-10 overflow-hidden"
        >
          {/* Mobile Vertical Timeline */}
          <div className="flex flex-col md:hidden space-y-6 px-4 max-w-md mx-auto">
            {projectSteps.map((step, index) => (
              <motion.div
                key={step.step}
                className="flex items-start gap-3 relative"
                initial="hidden"
                animate={controls}
                variants={{
                  hidden: { opacity: 0, x: -20 },
                  visible: {
                    opacity: 1,
                    x: 0,
                    transition: {
                      delay: index * 0.1,
                      duration: 0.5,
                    },
                  },
                }}
              >
                {/* Connection Line */}
                {index < projectSteps.length - 1 && (
                  <div className="absolute left-5 top-10 w-1 h-14 bg-gradient-to-b from-current to-blue-400" style={{ color: step.color.split(' ')[0] }}></div>
                )}

                {/* Step Circle with Number */}
                <div className="relative">
                  <div className={`${step.color} w-10 h-10 rounded-full flex items-center justify-center shadow-lg relative z-10`}>
                    <step.icon className="w-5 h-5" />
                  </div>
                  <div className="absolute -top-1 left-7 bg-white dark:bg-gray-800 rounded-full w-5 h-5 flex items-center justify-center text-xs font-bold border-2 border-primary text-primary z-20">
                    {step.step}
                  </div>
                </div>

                {/* Step Content */}
                <div className="pt-0">
                  <h3 className="text-base font-semibold text-gray-900 dark:text-white mb-0.5">
                    {step.title}
                  </h3>
                  <p className="text-xs text-gray-600 dark:text-gray-300">
                    {step.description}
                  </p>
                </div>
              </motion.div>
            ))}
          </div>

          {/* Desktop Grid Timeline - No Scrolling Required */}
          <div className="hidden md:block">
            <div className="max-w-6xl mx-auto">
              {/* Timeline line */}
              <div className="absolute h-1.5 bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 top-7 left-10 right-10 z-0 rounded-full opacity-70 mx-auto"/>

              <div className="grid grid-cols-5 gap-1">
                {projectSteps.slice(0, 5).map((step, index) => (
                  <motion.div
                    key={step.step}
                    className="relative px-2"
                    initial="hidden"
                    animate={controls}
                    variants={{
                      hidden: { opacity: 0, y: 20 },
                      visible: {
                        opacity: 1,
                        y: 0,
                        transition: {
                          delay: index * 0.1,
                          duration: 0.5,
                        },
                      },
                    }}
                  >
                    {/* Step Circle */}
                    <div className={`${step.color} w-14 h-14 rounded-full flex items-center justify-center shadow-md relative z-10 mx-auto mb-3`}>
                      <step.icon className="w-6 h-6" />
                    </div>

                    {/* Step Number */}
                    <div className="absolute top-0 right-8 -mt-2 -mr-2 bg-white dark:bg-gray-800 rounded-full w-7 h-7 flex items-center justify-center text-xs font-bold border-2 border-primary text-primary z-20 shadow-sm">
                      {step.step}
                    </div>

                    {/* Arrow Icon (except for the last item) */}
                    {index < 4 && (
                      <div className="absolute top-7 right-0 transform z-10">
                        <motion.div
                          animate={{ x: [0, 3, 0] }}
                          transition={{ duration: 1.2, repeat: Infinity, ease: "easeInOut", delay: index * 0.2 }}
                        >
                          <ArrowRight className="w-4 h-4 text-primary" />
                        </motion.div>
                      </div>
                    )}

                    {/* Step Content */}
                    <div className="text-center px-0">
                      <h3 className="text-sm font-semibold text-gray-900 dark:text-white mb-1">
                        {step.title}
                      </h3>
                      <p className="text-xs text-gray-600 dark:text-gray-300 leading-tight">
                        {step.description}
                      </p>
                    </div>
                  </motion.div>
                ))}
              </div>

              {/* Second row - with continuation line */}
              <div className="mt-12 relative">
                {/* Timeline connection line from first to second row */}
                <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 h-8 w-1.5 bg-gradient-to-b from-teal-500 to-blue-500 z-0 rounded-full opacity-70"/>

                {/* Timeline line for second row */}
                <div className="absolute h-1.5 bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 top-7 left-10 right-10 z-0 rounded-full opacity-70 mx-auto"/>

                <div className="grid grid-cols-5 gap-1">
                  {projectSteps.slice(5).map((step, index) => {
                    // Adjust index to account for the slice
                    const actualIndex = index + 5;
                    return (
                      <motion.div
                        key={step.step}
                        className="relative px-2"
                        initial="hidden"
                        animate={controls}
                        variants={{
                          hidden: { opacity: 0, y: 20 },
                          visible: {
                            opacity: 1,
                            y: 0,
                            transition: {
                              delay: actualIndex * 0.1,
                              duration: 0.5,
                            },
                          },
                        }}
                      >
                        {/* Step Circle */}
                        <div className={`${step.color} w-14 h-14 rounded-full flex items-center justify-center shadow-md relative z-10 mx-auto mb-3`}>
                          <step.icon className="w-6 h-6" />
                        </div>

                        {/* Step Number */}
                        <div className="absolute top-0 right-8 -mt-2 -mr-2 bg-white dark:bg-gray-800 rounded-full w-7 h-7 flex items-center justify-center text-xs font-bold border-2 border-primary text-primary z-20 shadow-sm">
                          {step.step}
                        </div>

                        {/* Arrow Icon (except for the last item) */}
                        {index < 4 && (
                          <div className="absolute top-7 right-0 transform z-10">
                            <motion.div
                              animate={{ x: [0, 3, 0] }}
                              transition={{ duration: 1.2, repeat: Infinity, ease: "easeInOut", delay: actualIndex * 0.2 }}
                            >
                              <ArrowRight className="w-4 h-4 text-primary" />
                            </motion.div>
                          </div>
                        )}

                        {/* Step Content */}
                        <div className="text-center px-0">
                          <h3 className="text-sm font-semibold text-gray-900 dark:text-white mb-1">
                            {step.title}
                          </h3>
                          <p className="text-xs text-gray-600 dark:text-gray-300 leading-tight">
                            {step.description}
                          </p>
                        </div>
                      </motion.div>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="max-w-3xl mx-auto text-center mt-12">
          <div className="bg-gradient-to-r from-primary/10 to-purple-500/10 backdrop-blur-sm p-6 rounded-xl border border-primary/20">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
              Ready to Start Your Project?
            </h3>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              I'll guide you through each step of the process, ensuring clear communication and exceptional results.
            </p>
            <div className="flex flex-wrap justify-center gap-4">
              <QuickQuoteModal 
                triggerClassName="px-5 py-2.5 rounded-lg bg-primary text-white font-medium transition-all hover:shadow-lg hover:bg-primary/90"
                icon={false}
                triggerText="Get a Quote"  
                buttonVariant={null}
              />
              <a
                href="tel:+918280320550"
                className="px-5 py-2.5 rounded-lg bg-white dark:bg-gray-800 text-primary dark:text-primary/80 border border-primary/30 font-medium transition-all hover:shadow-lg hover:bg-gray-50 dark:hover:bg-gray-700"
              >
                <Phone className="w-4 h-4 inline-block mr-2" />
                Call Directly
              </a>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

client/src/components/home/services-over.content:
import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { motion } from "framer-motion";
import { Service } from "@shared/schema";
import { 
  Code, 
  BarChart2, 
  Palette, 
  Search, 
  ShoppingCart, 
  Smartphone,
  ChevronRight,
  Brain,
  Link2,
  Copy,
  Wrench,
  Share2
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter } from "@/components/ui/card";
import ServiceRequestModal from "@/components/modals/service-request-modal";

const ServiceIcon = ({ name }: { name: string }) => {
  const icons = {
    "Code": <Code className="h-5 w-5" />,
    "BarChart": <BarChart2 className="h-5 w-5" />,
    "Palette": <Palette className="h-5 w-5" />,
    "Search": <Search className="h-5 w-5" />,
    "ShoppingCart": <ShoppingCart className="h-5 w-5" />,
    "Smartphone": <Smartphone className="h-5 w-5" />,
    "Brain": <Brain className="h-5 w-5" />,
    "Link": <Link2 className="h-5 w-5" />,
    "Copy": <Copy className="h-5 w-5" />,
    "Wrench": <Wrench className="h-5 w-5" />,
    "Share2": <Share2 className="h-5 w-5" />
  };

  return icons[name as keyof typeof icons] || <Code className="h-5 w-5" />;
};

const ServicesOverview = () => {
  const [isRequestModalOpen, setIsRequestModalOpen] = useState(false);
  const [selectedServiceId, setSelectedServiceId] = useState<string | undefined>(undefined);

  const openRequestModal = (serviceId: number) => {
    setSelectedServiceId(serviceId.toString());
    setIsRequestModalOpen(true);
  };

  const { data: services, isLoading, error } = useQuery<Service[]>({
    queryKey: ['/api/services/featured'],
    queryFn: async () => {
      try {
        const response = await fetch('/api/services/featured', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch featured services: ${response.statusText}`);
        }

        return await response.json();
      } catch (error) {
        console.error("Error fetching featured services:", error);
        throw error;
      }
    },
    refetchOnWindowFocus: false,
    retry: 2
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100 }
    }
  };

  return (
    <section className="py-20 bg-gray-50 dark:bg-gray-900/50">
      <div className="container px-4 mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold mb-4 text-gray-900 dark:text-white">
            Comprehensive Digital Services for Projects of All Sizes
          </h2>
          <p className="text-lg text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            I offer a complete range of fast, reliable, and cutting-edge solutions to help your business thrive in the digital landscape.
            From stunning websites and AI integration to mobile apps and strategic marketing campaigns.
            All delivered with a focus on reliability, speed, and competitive pricing for every budget.
          </p>
        </div>

        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[...Array(3)].map((_, i) => (
              <Card key={i} className="border border-gray-200 dark:border-gray-800 bg-card animate-pulse h-72">
                <div className="p-6 flex flex-col h-full">
                  <div className="h-6 bg-gray-200 dark:bg-gray-700 rounded w-2/3 mb-3"></div>
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-5/6 mb-2"></div>
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-full mb-2"></div>
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-4/5"></div>
                  <div className="mt-auto h-10 bg-gray-200 dark:bg-gray-700 rounded w-1/3"></div>
                </div>
              </Card>
            ))}
          </div>
        ) : error ? (
          <div className="text-center p-8 bg-red-50 dark:bg-red-900/20 rounded-lg">
            <p className="text-red-600 dark:text-red-400">Failed to load services. Please try again later.</p>
          </div>
        ) : (
          <motion.div 
            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-100px" }}
          >
            {services?.map((service) => (
              <motion.div key={service.id} variants={itemVariants}>
                <Card className="border border-gray-200 dark:border-gray-800 bg-card h-full hover:shadow-md transition-shadow duration-300">
                  <CardHeader>
                    <div className="w-12 h-12 rounded-lg bg-primary/10 flex items-center justify-center mb-4">
                      <ServiceIcon name={service.iconName} />
                    </div>
                    <CardTitle className="text-xl font-bold">{service.title}</CardTitle>
                    <CardDescription>{service.shortDescription}</CardDescription>
                  </CardHeader>
                  <CardContent className="text-sm text-gray-600 dark:text-gray-400">
                    {/* Display a truncated version of the full description */}
                    {service.fullDescription.split(' ').slice(0, 20).join(' ')}...
                  </CardContent>
                  <CardFooter className="flex flex-col space-y-2">
                    <div className="flex justify-between w-full">
                      <Link href={`/${service.slug}`}>
                        <Button variant="ghost" size="sm" className="text-primary dark:text-primary-foreground">
                          Learn more <ChevronRight className="ml-1 h-4 w-4" />
                        </Button>
                      </Link>
                      <Button 
                        variant="outline" 
                        size="sm" 
                        className="text-primary border-primary/30 hover:bg-primary/5"
                        onClick={() => openRequestModal(service.id)}
                      >
                        Request this service
                      </Button>
                    </div>
                  </CardFooter>
                </Card>
              </motion.div>
            ))}
          </motion.div>
        )}

        <div className="mt-12 text-center">
          <Link href="/services">
            <Button variant="outline" size="lg">
              View All Services
            </Button>
          </Link>
        </div>
      </div>

      {/* Service Request Modal */}
      <ServiceRequestModal
        isOpen={isRequestModalOpen}
        onClose={() => setIsRequestModalOpen(false)}
        serviceId={selectedServiceId}
      />
    </section>
  );
};

export default ServicesOverview;

