import React, { useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { queryClient } from '@/lib/queryClient';

declare global {
  interface Window {
    Razorpay: any;
  }
}

interface RazorpayCheckoutProps {
  orderId: string;
  amount: number;
  onCancel: () => void;
  name?: string;
  email?: string;
  onSuccess?: () => void;
}

export default function RazorpayCheckout({
  orderId,
  amount,
  onCancel,
  name = '',
  email = '',
  onSuccess
}: RazorpayCheckoutProps) {
  const { toast } = useToast();

  // Load Razorpay script
  useEffect(() => {
    const loadRazorpayScript = () => {
      return new Promise((resolve) => {
        const script = document.createElement('script');
        script.src = 'https://checkout.razorpay.com/v1/checkout.js';
        script.onload = () => {
          resolve(true);
        };
        script.onerror = () => {
          resolve(false);
          toast({
            title: 'Error',
            description: 'Failed to load Razorpay. Please try another payment method.',
            variant: 'destructive',
          });
        };
        document.body.appendChild(script);
      });
    };

    // Load script only if not already loaded
    if (!window.Razorpay) {
      loadRazorpayScript();
    }
  }, [toast]);

  const openRazorpayCheckout = async () => {
    try {
      // Safely load Razorpay script first if not loaded
      if (!window.Razorpay) {
        await new Promise<boolean>((resolve) => {
          const script = document.createElement('script');
          script.src = 'https://checkout.razorpay.com/v1/checkout.js';

          script.onload = () => {
            console.log('Razorpay script loaded successfully');
            resolve(true);
          };

          script.onerror = () => {
            console.error('Failed to load Razorpay script');
            toast({
              title: 'Error',
              description: 'Failed to load Razorpay. Please try another payment method.',
              variant: 'destructive',
            });
            resolve(false);
          };

          document.body.appendChild(script);
        });
      }

      if (!window.Razorpay) {
        throw new Error('Razorpay failed to load');
      }

      // Fetch key ID from the server
      const keyResponse = await fetch('/api/payment/status');
      const keyData = await keyResponse.json();
      const keyId = keyData.razorpay.keyId;

      console.log('Razorpay key ID:', keyId ? 'Available' : 'Missing');

      if (!keyId) {
        throw new Error('Razorpay key not available');
      }

      const options = {
        key: keyId,
        amount: amount * 100, // Convert to paise
        currency: 'INR',
        name: 'Samuel Marndi',
        description: 'Payment for services',
        order_id: orderId,
        prefill: {
          name: name || 'Customer',
          email: email || 'customer@example.com',
        },
        theme: {
          color: '#3182ce',
        },
        handler: function (response: any) {
          // Handle successful payment
          toast({
            title: 'Payment Successful',
            description: `Payment ID: ${response.razorpay_payment_id}`,
          });

          // Verify payment on the server
          fetch('/api/payment/razorpay/verify-payment', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              order_id: response.razorpay_order_id,
              payment_id: response.razorpay_payment_id,
              signature: response.razorpay_signature,
            }),
          })
            .then(res => res.json())
            .then(data => {
              if (data.verified) {
                // Invalidate any related queries
                queryClient.invalidateQueries();

                if (onSuccess) {
                  onSuccess();
                }
              } else {
                toast({
                  title: 'Verification Failed',
                  description: 'Payment verification failed. Please contact support.',
                  variant: 'destructive',
                });
              }
            })
            .catch(err => {
              console.error('Verification error:', err);
              toast({
                title: 'Verification Error',
                description: 'There was an error verifying your payment.',
                variant: 'destructive',
              });
            });
        },
        modal: {
          ondismiss: function () {
            toast({
              title: 'Payment Cancelled',
              description: 'You have cancelled the payment process.',
            });
          },
        },
      };

      console.log('Initializing Razorpay checkout with options:', {
        ...options,
        key: 'HIDDEN', // Don't log the key in client console
      });

      try {
        const razorpay = new window.Razorpay(options);

        // Add error event listener
        razorpay.on('payment.failed', function(failedResponse: any) {
          console.error('Razorpay payment failed:', failedResponse);
          toast({
            title: 'Payment Failed',
            description: failedResponse.error?.description || 'Your payment attempt failed. Please try again or use a different payment method.',
            variant: 'destructive',
          });
        });

        razorpay.open();
      } catch (razorpayError) {
        console.error('Error opening Razorpay:', razorpayError);
        toast({
          title: 'Checkout Error',
          description: 'Unable to open Razorpay checkout. Please try again later.',
          variant: 'destructive',
        });
      }
    } catch (error) {
      console.error('Razorpay error:', error);
      toast({
        title: 'Payment Error',
        description: 'Failed to initialize payment. Please try another payment method.',
        variant: 'destructive',
      });
    }
  };

  return (
    <div className="space-y-4">
      <div className="p-4 rounded-md bg-blue-50 border border-blue-100">
        <h3 className="text-lg font-medium mb-2">Complete Your Payment</h3>
        <p className="text-sm text-gray-600 mb-4">
          Pay ₹{amount.toFixed(2)} securely through Razorpay's payment gateway.
        </p>
        <div className="flex space-x-4">
          <Button 
            onClick={openRazorpayCheckout} 
            className="w-full"
          >
            Pay Now
          </Button>
          <Button 
            variant="outline" 
            onClick={onCancel}
          >
            Cancel
          </Button>
        </div>
      </div>

      <div className="flex items-center justify-center space-x-2 text-sm text-gray-500">
        <svg className="h-4 w-4" fill="currentColor" viewBox="0 0 20 20">
          <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
        </svg>
        <span>Secured by Razorpay</span>
      </div>
    </div>
  );
}

client/src/components/payment/simple-pay.content:
import React, { useState } from 'react';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import { useForm } from 'react-hook-form';
import { 
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { Card, CardContent } from '@/components/ui/card';
import { Loader2 } from 'lucide-react';
import { useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import StripeCheckout from './stripe-checkout';
import PayPalCheckout from './paypal-checkout';
import RazorpayCheckout from './razorpay-checkout';
import UpiPayment from './upi-payment';

// Define form schema
const simplePaymentSchema = z.object({
  amount: z.string().min(1, 'Amount is required').regex(/^\d+(\.\d{1,2})?$/, 'Invalid amount format'),
  paymentFor: z.string().min(5, 'Please describe what this payment is for'),
  paymentMethod: z.enum(['stripe', 'paypal', 'razorpay', 'upi']),
  name: z.string().min(2, 'Name is required'),
  email: z.string().email('Please enter a valid email address'),
});

type SimplePaymentFormValues = z.infer<typeof simplePaymentSchema>;

interface SimplePaymentFormProps {
  gatewayStatus: any;
}

export default function SimplePaymentForm({ gatewayStatus }: SimplePaymentFormProps) {
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState(1);
  const [paymentData, setPaymentData] = useState<any>(null);

  // Form definition
  const form = useForm<SimplePaymentFormValues>({
    resolver: zodResolver(simplePaymentSchema),
    defaultValues: {
      amount: '',
      paymentFor: '',
      paymentMethod: 'upi', // Set UPI as default since it's the most reliable option
      name: '',
      email: '',
    },
  });

  // Mutation for payment initialization
  const { mutate: initializePayment, isPending } = useMutation({
    mutationFn: async (data: SimplePaymentFormValues) => {
      let response;

      switch (data.paymentMethod) {
        case 'stripe':
          response = await apiRequest(
            'POST', 
            '/api/payment/stripe/create-intent', 
            { 
              amount: parseFloat(data.amount),
              description: data.paymentFor,
              name: data.name,
              email: data.email
            }
          );
          break;
        case 'paypal':
          response = await apiRequest(
            'POST', 
            '/api/payment/paypal/create-order', 
            { 
              amount: data.amount,
              currency: 'INR',
              intent: 'CAPTURE'
            }
          );
          break;
        case 'razorpay':
          response = await apiRequest(
            'POST', 
            '/api/payment/razorpay/create-order', 
            { 
              amount: parseFloat(data.amount),
              currency: 'INR',
              receipt: `payment_${Date.now()}`,
              notes: {
                paymentFor: data.paymentFor,
                name: data.name,
                email: data.email
              }
            }
          );
          break;
        case 'upi':
          // For UPI, just create a dummy response with the UPI ID
          // No need to call the server for UPI payments since we're using a fixed UPI ID
          try {
            // Record this payment attempt if the endpoint exists
            await apiRequest(
              'POST', 
              '/api/payment/upi/record', 
              { 
                amount: parseFloat(data.amount),
                description: data.paymentFor,
                name: data.name,
                email: data.email,
                status: 'initiated'
              }
            ).catch(err => {
              // Silently handle error - this is optional functionality
              console.log('UPI payment record endpoint not available:', err);
            });

            // Create a dummy response object with UPI details
            response = new Response(JSON.stringify({
              success: true,
              upiId: "8280320550@axisb",
              amount: data.amount,
              name: data.name
            }));
          } catch (error) {
            console.error('Error handling UPI payment:', error);
            // Still return a valid response so UPI payment can proceed
            response = new Response(JSON.stringify({
              success: true,
              upiId: "8280320550@axisb"
            }));
          }
          break;
      }

      // If response is undefined (shouldn't happen), return an empty object
      return response ? await response.json() : {};
    },
    onSuccess: (data) => {
      toast({
        title: 'Payment Initialized',
        description: 'Payment gateway ready',
      });

      // Save the payment data for the checkout components
      setPaymentData(data);

      // Move to payment step
      setCurrentStep(3);
    },
    onError: (error: any) => {
      toast({
        variant: 'destructive',
        title: 'Payment initialization failed',
        description: error.message || 'Please try again or use a different payment method.',
      });
    },
  });

  function onSubmit(data: SimplePaymentFormValues) {
    if (currentStep === 1) {
      setCurrentStep(2); // Move to review step
    } else if (currentStep === 2) {
      initializePayment(data);
    }
  }

  const availableGateways = gatewayStatus ? {
    stripe: gatewayStatus.stripe.available,
    paypal: gatewayStatus.paypal.available,
    razorpay: gatewayStatus.razorpay.available,
    upi: true // UPI is always available
  } : { 
    stripe: false, 
    paypal: false, 
    razorpay: false,
    upi: true // UPI is always available
  };

  // If no gateways are available (UPI is always available, so this should never happen)
  if (!availableGateways.stripe && !availableGateways.paypal && !availableGateways.razorpay && !availableGateways.upi) {
    return (
      <div className="text-center p-4 border rounded-md bg-muted/50">
        <p>Payment gateways are currently unavailable. Please contact me directly to discuss payment options.</p>
        <Button className="mt-4" asChild>
          <a href="/contact">Contact Me</a>
        </Button>
      </div>
    );
  }

  if (currentStep === 1) {
    return (
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6 mx-auto max-w-md">
          <FormField
            control={form.control}
            name="amount"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Amount (₹)</FormLabel>
                <FormControl>
                  <Input placeholder="1000.00" {...field} />
                </FormControl>
                <FormDescription>
                  Enter the amount you'd like to pay in Indian Rupees (INR)
                </FormDescription>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="paymentFor"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Payment Purpose</FormLabel>
                <FormControl>
                  <Textarea 
                    placeholder="e.g., Website development project, SEO services" 
                    {...field} 
                  />
                </FormControl>
                <FormDescription>
                  Briefly describe what this payment is for
                </FormDescription>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="name"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Your Name</FormLabel>
                <FormControl>
                  <Input placeholder="John Doe" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="email"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Email Address</FormLabel>
                <FormControl>
                  <Input placeholder="you@example.com" type="email" {...field} />
                </FormControl>
                <FormDescription>
                  Receipt will be sent to this email
                </FormDescription>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="paymentMethod"
            render={({ field }) => (
              <FormItem className="space-y-3">
                <FormLabel>Payment Method</FormLabel>
                <FormControl>
                  <RadioGroup
                    onValueChange={field.onChange}
                    defaultValue={field.value}
                    className="flex flex-col space-y-1"
                  >
                    {/* Make UPI first and most prominent */}
                    <div className="flex items-center space-x-2 p-3 bg-green-50 dark:bg-green-900/20 rounded-md border border-green-100 dark:border-green-900">
                      <RadioGroupItem value="upi" id="upi" />
                      <Label htmlFor="upi" className="font-medium flex flex-col">
                        <span className="flex items-center">UPI Payment <span className="ml-2 text-xs px-1.5 py-0.5 bg-green-100 dark:bg-green-800 text-green-800 dark:text-green-100 rounded">Recommended</span></span>
                        <span className="text-xs text-muted-foreground">Pay directly using any UPI app (PhonePe, Google Pay, BHIM, Paytm)</span>
                      </Label>
                    </div>

                    {availableGateways.stripe && (
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="stripe" id="stripe" />
                        <Label htmlFor="stripe" className="font-normal">
                          Credit/Debit Card (Stripe)
                        </Label>
                      </div>
                    )}

                    {availableGateways.paypal && (
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="paypal" id="paypal" />
                        <Label htmlFor="paypal" className="font-normal">
                          PayPal
                        </Label>
                      </div>
                    )}

                    {availableGateways.razorpay && (
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="razorpay" id="razorpay" />
                        <Label htmlFor="razorpay" className="font-normal">
                          Razorpay (Cards, Netbanking, Wallets)
                        </Label>
                      </div>
                    )}
                  </RadioGroup>
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <Button type="submit">
            Review Payment
          </Button>
        </form>
      </Form>
    );
  }

  // Check if we're in the payment step (step 3)
  if (currentStep === 3 && paymentData) {
    const formValues = form.getValues();
    const amount = parseFloat(formValues.amount);

    // Show appropriate payment component based on selected method
    if (formValues.paymentMethod === 'stripe' && paymentData.clientSecret) {
      return (
        <div className="space-y-6">
          <StripeCheckout 
            clientSecret={paymentData.clientSecret} 
            amount={amount}
            onCancel={() => setCurrentStep(2)}
          />
        </div>
      );
    }

    if (formValues.paymentMethod === 'paypal' && paymentData.id) {
      return (
        <div className="space-y-6">
          <PayPalCheckout 
            orderId={paymentData.id} 
            amount={amount}
            onCancel={() => setCurrentStep(2)}
          />
        </div>
      );
    }

    if (formValues.paymentMethod === 'razorpay' && paymentData.id) {
      return (
        <div className="space-y-6">
          <RazorpayCheckout
            orderId={paymentData.id}
            amount={amount}
            name={formValues.name}
            email={formValues.email}
            onCancel={() => setCurrentStep(2)}
          />
        </div>
      );
    }

    if (formValues.paymentMethod === 'upi') {
      return (
        <div className="space-y-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-medium">UPI Payment</h3>
            <Button variant="outline" size="sm" onClick={() => setCurrentStep(2)}>
              Go Back
            </Button>
          </div>

          {/* Use the existing UPI payment component with hardcoded UPI ID */}
          <UpiPayment 
            upiId="8280320550@axisb" 
          />

          <div className="p-4 bg-amber-50 border border-amber-100 dark:bg-amber-900/20 dark:border-amber-800 rounded-md mt-4">
            <p className="text-sm text-amber-800 dark:text-amber-300">
              <span className="font-medium">Important:</span> After making the payment, please contact us with your payment details for verification.
            </p>
          </div>
        </div>
      );
    }

    // Fallback if something went wrong
    return (
      <div className="space-y-6">
        <div className="text-center p-8 border rounded-md">
          <p className="text-red-500 mb-4">There was an issue initializing the payment gateway.</p>
          <Button onClick={() => setCurrentStep(2)}>Go Back</Button>
        </div>
      </div>
    );
  }

  // Review step (step 2)
  const formValues = form.getValues();
  return (
    <div className="space-y-6 mx-auto max-w-md">
      <Card>
        <CardContent className="pt-6">
          <div className="space-y-4">
            <div className="flex justify-between border-b pb-2">
              <span className="font-medium">Amount:</span>
              <span>₹{formValues.amount}</span>
            </div>

            <div className="flex justify-between border-b pb-2">
              <span className="font-medium">Payment Purpose:</span>
              <span>{formValues.paymentFor}</span>
            </div>

            <div className="flex justify-between border-b pb-2">
              <span className="font-medium">Name:</span>
              <span>{formValues.name}</span>
            </div>

            <div className="flex justify-between border-b pb-2">
              <span className="font-medium">Email:</span>
              <span>{formValues.email}</span>
            </div>

            <div className="flex justify-between">
              <span className="font-medium">Payment Method:</span>
              <span>
                {formValues.paymentMethod === 'stripe' && 'Credit/Debit Card (Stripe)'}
                {formValues.paymentMethod === 'paypal' && 'PayPal'}
                {formValues.paymentMethod === 'razorpay' && 'Razorpay (UPI, Netbanking, Wallets)'}
              </span>
            </div>
          </div>
        </CardContent>
      </Card>

      <div className="flex space-x-4 justify-center">
        <Button variant="outline" onClick={() => setCurrentStep(1)}>
          Back
        </Button>
        <Button 
          onClick={form.handleSubmit(onSubmit)} 
          disabled={isPending}
        >
          {isPending ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Processing...
            </>
          ) : (
            'Proceed to Payment'
          )}
        </Button>
      </div>
    </div>
  );
}

